import React, { useState, useEffect } from 'react';
import getKey from '../utils/KeyGenerator.js';

const TripFuelTracker = () => {

    const tripsTemplate = '[{"location":"La Costa","time":"10/24/2023, 6:27:42 PM","gallons":"21","usdPerGallon":"4.50","totalUSD":"94.50"},{"location":"CaliMex","time":"10/24/2023, 7:29:00 PM","gallons":"21","usdPerGallon":"5.50","totalUSD":"115.50"},{"location":"San Felipe","time":"10/24/2023, 7:36:10 PM","gallons":"21.1","usdPerGallon":"4.98","totalUSD":"105.26"}]';

    localStorage.setItem('TripFuelTracker', tripsTemplate);

    const [location, setLocation] = useState('');
    const [exchangeRate, setExchangeRate] = useState(0);
    const [pricePerLiter, setPricePerLiter] = useState(0);
    const [pricePerGallon, setPricePerGallon] = useState(0);
    const [gallonsPurchased, setGallonsPurchased] = useState(0);
    const [litersPurchased, setLitersPurchased] = useState(0);
    const [trips, setTrips] = useState(JSON.parse(localStorage.getItem('TripFuelTracker')) || []);
    const [currency, setCurrency] = useState('USD');

    const currencyCode = [
        'USD',
        'MXN',
        'NIO',
        'CRC',
        'IDR',
        'AUD',
    ];

    const currencies = {
        USD: 'Dollar',
        MXN: `Pesos`,   // Mexican Peso
        NIO: `Córdoba`,   // Nicaraguan Cordovas
        CRC: `Colones`,    // Costa Rican Colones
        IDR: `Rupiah`,  // Indonesian Rupiah
        AUD: `AUD`,    // Australian Dollar
    };

    useEffect(() => {
        localStorage.setItem('TripFuelTracker', JSON.stringify(trips))
    }, [trips]);

    const clearRecord = () => {
        setTrips([]);
    }
    const calculateAndRecord = () => {

        const pricePerLiterUSD = pricePerLiter / exchangeRate;
        const gallons = litersPurchased * 0.264172;
        const newGallons = (currency === 'USD') ? gallonsPurchased : gallons.toFixed(1);

        const usdPerGallon = (currency === 'USD') ? Number(pricePerGallon) : Number(pricePerLiterUSD * 3.78541);
        const newUsdPerGallon = usdPerGallon.toFixed(2) || 0;
        const totalUSD = (currency === 'USD') ? (gallonsPurchased * pricePerGallon) : (gallons * usdPerGallon);
        const newTotalUSD = totalUSD.toFixed(2);
        const time = new Date().toLocaleString();

        const newTrip = {
            location: location,
            time: time,
            gallons: newGallons,
            usdPerGallon: newUsdPerGallon,
            totalUSD: newTotalUSD
        }
        const newTrips = [...trips, newTrip]
        setTrips(newTrips);

    };
    const editLocation = (index, location) => {
        const newTrips = [...trips];
        newTrips[index].location = prompt('Edit locartion:', location) || location;
        setTrips(newTrips);
    }
    const editGallons = (index, gallons) => {
        const newTrips = [...trips];
        newTrips[index].gallons = prompt('Edit gallons:', gallons) || gallons;
        newTrips[index].totalUSD = newTrips[index].usdPerGallon * newTrips[index].gallons;
        setTrips(newTrips);
    }
    const editUSDGallons = (index, usdPerGallon) => {
        const newTrips = [...trips];
        newTrips[index].usdPerGallon = prompt('Edit USD per gallon', usdPerGallon) || usdPerGallon;
        newTrips[index].totalUSD = newTrips[index].usdPerGallon * newTrips[index].gallons;
        setTrips(newTrips);
    }
    const editTotalUSD = (index, totalUSD) => {
        const newTrips = [...trips];
        newTrips[index].totalUSD = prompt('Edit total USD:', totalUSD) || totalUSD;
        setTrips(newTrips);
    }
    const getTotalGallons = () => {
        const totalGallons = trips.reduce((accumulator, trip) => accumulator + parseFloat(trip.gallons), 0);
        return totalGallons.toFixed(1);
    }

    const getTotalUSD = () => {
        const totalUSD = trips.reduce((accumulator, trip) => accumulator + parseFloat(trip.totalUSD), 0);
        return totalUSD.toFixed(2);
    };
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setCurrency(value);
    };
    const getUSInputs = () => {
        if (currencies[currency] === 'Dollar') {
            return <div>
                        <label className='flexContainer bg-veryLite r-10 m-5'>
                            <div className='columnRightAlign width-50-percent'>
                                <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                                    {currencies[currency]} per Gallon:
                                </span>
                            </div>
                            <div className='size20 p-10 columnLeftAlign width-50-percent'>
                                <input className='r-5 p-10 w-150 bg-darker color-soft'
                                    type='number'
                                    value={pricePerGallon}
                                    onChange={(e) => setPricePerGallon(e.target.value)}
                                />
                            </div>
                        </label>
                        <label className='flexContainer bg-veryLite r-10 m-5'>
                            <div className='columnRightAlign width-50-percent'>
                                <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                                    Total Gallons:
                                </span>
                            </div>
                            <div className='size20 p-10 columnLeftAlign width-50-percent'>
                                <input className='r-5 p-10 w-150 bg-darker color-soft'
                                    type='number'
                                    value={gallonsPurchased}
                                    onChange={(e) => setGallonsPurchased(e.target.value)}
                                />
                            </div>
                        </label>
                     </div>
        }
    }
    const getInternationalInputs = () => {
        if (currencies[currency] !== 'Dollar') {
            return <div>
                        <label className='flexContainer bg-veryLite r-10 m-5'>
                            <div className='columnRightAlign width-50-percent'>
                                <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                                    Exchange Rate:
                                </span>
                            </div>
                            <div className='size20 p-10 columnLeftAlign width-50-percent'>
                                <input className='r-5 p-10 w-150 bg-darker color-soft'
                                    type='number'
                                    value={exchangeRate}
                                    onChange={(e) => setExchangeRate(e.target.value)}
                                />
                            </div>
                        </label>
                        <label className='flexContainer bg-veryLite r-10 m-5'>
                            <div className='columnRightAlign width-50-percent'>
                                <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                                    {currencies[currency]} per Liter:
                                </span>
                            </div>
                            <div className='size20 p-10 columnLeftAlign width-50-percent'>
                                <input className='r-5 p-10 w-150 bg-darker color-soft'
                                    type='number'
                                    value={pricePerLiter}
                                    onChange={(e) => setPricePerLiter(e.target.value)}
                                />
                            </div>
                        </label>
                        <label className='flexContainer bg-veryLite r-10 m-5'>
                            <div className='columnRightAlign width-50-percent'>
                                <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                                    Total Liters:
                                </span>
                            </div>
                            <div className='size20 p-10 columnLeftAlign width-50-percent'>
                                <input className='r-5 p-10 w-150 bg-darker color-soft'
                                    type='number'
                                    value={litersPurchased}
                                    onChange={(e) => setLitersPurchased(e.target.value)}
                                />
                            </div>
                        </label>
                     </div>
        }
    }

    return (
        <div className='mt--20'>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                        Location:
                    </span>
                </div>
                <div className='size20 p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-150 bg-darker color-soft'
                        type='string'
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='size20 bg-tinted r-5 w-200 p-10 bold color-soft'>
                        Currency:
                    </span>
                </div>
                <div className='size20 p-10 columnLeftAlign width-50-percent'>
                    <select
                        name="currency"
                        value={currency}
                        onChange={handleInputChange}
                        className='mt--2 w-150 bg-darker color-soft r-5 p-10'
                    >
                        <option value="">Select Currency</option>
                        {currencyCode.map((currency) => (
                            <option key={currency} value={currency}>
                                {currencies[currency]}
                            </option>
                        ))}
                    </select>
                </div>
            </label>
            {getInternationalInputs()}
            {getUSInputs()}
            <div className='flexContainer'>
                <div className='flex2Column'>
                    <button className='myButton mt-20 p-20 w-150' onClick={calculateAndRecord}>Add</button>
                </div>
                <div className='flex2Column'>
                    <button className='myButton mt-20 p-20 w-150' onClick={clearRecord}>Clear</button>
                </div>
            </div>
            <div className='scrollHeight250 bg-darker r-5 m-5 mt-20'>
                {
                    trips.map((trip, index) => <div className='mt-1 color-soft' key={getKey(`trip${index}`)}>
                        <div className='flexContainer bg-tinted r-5 m-5 p-5'>
                            <div>
                                <div className='p-5 bold mt-5'>{index + 1}</div>
                            </div>
                            <div className='m-auto'>
                                <div className='p-5 bold r-5 mt-5' onClick={() => editLocation(index, trip.location)}>
                                    {trip.location}
                                </div>
                                <div className='copyright'>{trip.time.split(', ')[0] || 0}</div>
                                <div className='copyright mt--5'>{trip.time.split(', ')[1] || 0}</div>
                            </div>
                            <div className='m-auto'>
                                <div className='p-5 mt-5'>Gallons</div>
                                <div className='p-5 bold bg-darker r-5 size30' onClick={() => editGallons(index, trip.gallons)}>{trip.gallons}</div>
                            </div>
                            <div className='m-auto'>
                                <div className='p-5 mt-5'>$/Gallons</div>
                                <div className='p-5 bold bg-darker r-5 size30' onClick={() => editUSDGallons(index, trip.usdPerGallon)}>${trip.usdPerGallon}</div>
                            </div>
                            <div>
                                <div className='p-5 mt-5'>USD</div>
                                <div className='p-5 bold bg-darker r-5 size30' onClick={() => editTotalUSD(index, trip.totalUSD)}>${trip.totalUSD}</div> 
                            </div>
                        </div>
                    </div>
                    )
                }
            </div>
            <div className='flexContainer bg-veryLite color-yellow mt-20 r-5 m-5 p-5 size20'>
                <div className='flex3Column columnLeftAlign'>
                    <span className='p-5 bold'>TRIP TOTALS:</span>
                </div>
                <div className='flex3Column columnLeftAlign'>
                <span className='p-5'>gallons</span>
                    <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>{getTotalGallons()}</span>
                </div>
                <div className='flex3Column columnLeftAlign'>
                    <span className='p-5'>USD</span>
                    <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>${getTotalUSD()}</span>
                </div>
            </div>
        </div>
    );
};

export default TripFuelTracker;
