import React, { useState } from 'react';

const Accordion = ({ items }) => {
    const [ selectedIndex, setSelectedIndex ] = useState(null);
    const clicked = (index) => setSelectedIndex(index);
    const renderedItems = items.map((item, index) => {
        return <div key={item.item} className='button pointer ml-20 mr-20 mb-1 bg-darker r-5 p-10'>
                    <div className='greet color-yellow active' onClick={() => clicked(index)}>
                        <i className='dropDown icon'></i>
                        {item.item}
                    </div>
                    <div className='white description active'>
                        <p>{item.content}</p>
                    </div>
            </div>
    })
    return <div className='accordion'>
            {renderedItems}
        </div>
};

export default Accordion;
