import React, { useState, useEffect } from 'react';
import getKey from '../utils/KeyGenerator.js';
import Sounds from '../utils/Sounds.js';

export default function Todos(props) {
    const initTodos = [
        {
            description: 'Eat',
            type: 'checkbox',
            time: null,
            activated: false,
            completed: false
        },
        {
            description: 'Surf',
            type: 'track',
            time: 0,
            activated: false,
            completed: false
        },
        {
            description: 'Sleep',
            type: 'timer',
            time: 20,
            activated: false,
            completed: false
        }
    ]
    const getLocal = () => (localStorage.getItem('todos') === 'null') ? initTodos : JSON.parse(localStorage.getItem('todos'));
    const [todos, setTodos] = useState(getLocal());
    const [edit, setEdit] = useState(false);
    const storeIt = () => localStorage.setItem('todos', JSON.stringify(todos));
    const todoInput = () => document.getElementById('todo').value;
    const clearInput = () => document.getElementById('todo').value = '';
    const getNewTodo = (todo) => todo.charAt(0).toUpperCase() + todo.slice(1);

    useEffect(() => {
        storeIt();
    }, [todos]);

    const addTodo = () => {
        const newTodo = {
            description: getNewTodo(todoInput()),
            completed: false
        }
        console.log(`new todo: ${newTodo.description}`);
        const newTodos = [...todos];
        newTodos.push(newTodo);
        setTodos(newTodos);
        clearInput();
        Sounds.playSound(0, 1);
    };
    const removeTodo = (id) => {
        console.log(`remove todo: ${todos[id]}`);
        const newTodos = [...todos];
        newTodos.splice(id, 1)
        setTodos(newTodos);
        Sounds.playSound(0, 1);
    }
    const editTodo = (id) => {
        console.log(`edit todo: ${todos[id]}`);
        const newTodos = [...todos];
        const modifiedTodo = prompt('description: ', newTodos[id].description)
        newTodos[id].description = modifiedTodo;
        setTodos(newTodos);
        setEdit(!edit);
        Sounds.playSound(0, 1);
    }
    const trackTodo = (id) => {
        console.log(`time todo: ${todos[id]}`);
        const newTodos = [...todos];
        newTodos[id].type = 'track';
        newTodos[id].time = 0;
        setTodos(newTodos);
        setEdit(!edit);
        Sounds.playSound(0, 1);
    }
    const timerTodo = (id) => {
        console.log(`edit todo: ${todos[id]}`);
        const newTodos = [...todos];
        newTodos[id].type = 'timer';
        newTodos[id].time = 0;
        const modifiedTodo = prompt('enter time (minutes): ', newTodos[id].time)
        newTodos[id].time = modifiedTodo;
        setTodos(newTodos);
        setEdit(!edit);
        Sounds.playSound(0, 1);
    }
    const toggleTimer = (id) => {
        console.log(`start timer: ${todos[id]}`);
        const newTodos = [...todos];
        newTodos[id].activated = !newTodos[id].activated;
        setTodos(newTodos);
        setEdit(!edit);
        Sounds.playSound(0, 1);
    }
    const getClock = (id) => {
        const todoTime = todos[id].time;
        return <span>{todoTime}</span>
    }
    function makeMenu() {
        const makeMenu = (menu) => menu.map(item => <div className='button bg-green r-10 m-5 p-5'>{item}</div>);
        const menuData = ['one', 'two', 'thee'];
        return (
            <React.Fragment>{makeMenu(menuData)}</React.Fragment>
        )
    }
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            addTodo();
        }
    };
    const toggleCheckbox = (index) => {
        const newTodos = [...todos];
        newTodos[index].completed = !newTodos[index].completed;
        setTodos(newTodos);
        Sounds.playSound(0, 1);
    };
    const reset = () => {
        const newTodos = [...todos];
        newTodos.map((todo) => todo.completed = false);
        setTodos(newTodos);
        Sounds.playSound(0, 1);
    }
    const editTodos = () => {
        setEdit(!edit);
        Sounds.playSound(0, 1);
    }
    const clear = () => {
        setTodos([]);
        Sounds.playSound(0, 1);
    }
    return (
        <div className='mt--30'>
            <div className='color-yellow p-5 r-10'>
                <div className='p-5 r-10 bg-tinted'>
                    <label className='flexContainer'>
                        <input className='ht-30 m-5 flex2Column contentCenter p-15 r-10 bg-darker color-white bold size25' type='text' id='todo' name='todo' placeholder='Enter a todo' onKeyDown={handleKeyDown} />
                        <button className='r-10 greet bold bg-green m-5 p-10 flex2Column10Percent contentCenter' onClick={() => addTodo()}>ADD</button>
                    </label>
                </div>
            </div>
            <div className='p-5 r-10'>
                <div id='list' className='p-5 r-10 bg-tinted'>
                    {todos.map((todo, index) => (
                        <div key={getKey('todo')} className='p-10 flexContainer bg-darker r-10 m-5'>
                            <div className='color-lite flex2Column p-10 size25 columnLeftAlign'>
                                <input className='regular-checkbox button mr-10' checked={todo.completed} type='checkbox' id='completed' onChange={() => toggleCheckbox(index)} />
                                {todo.description}
                            </div>
                            <div className='flex2Column10Percent columnRightAlign'>
                                {
                                    (todo.type == 'timer' || todo.type == 'track')
                                    ? <div>
                                        <button id={`toggleTimer${index}`} name={`toggleTimer${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => toggleTimer(index)}>
                                            {(todo.activated)?'stop':'start'}
                                        </button>
                                        <div>
                                            {getClock(id)}
                                        </div>
                                    </div>
                                    : null

                                    (edit)
                                    ? <div>
                                        <button id={`setTimert${index}`} name={`setTimer${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => timerTodo(index)}>set timer</button>
                                        <button id={`time${index}`} name={`time${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => trackTodo(index)}>track time</button>
                                        <button id={`edit${index}`} name={`edit${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => editTodo(index)}>edit</button>
                                        <button id={`remove${index}`} name={`remove${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => removeTodo(index)}>X</button>
                                    </div>
                                    : null

                                    (edit)
                                    ? <div>
                                        <button id={`setTimert${index}`} name={`setTimer${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => timerTodo(index)}>set timer</button>
                                        <button id={`time${index}`} name={`time${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => trackTodo(index)}>track time</button>
                                        <button id={`edit${index}`} name={`edit${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => editTodo(index)}>edit</button>
                                        <button id={`remove${index}`} name={`remove${todo.description}`} className='r-10 p-15 greet bold bg-tinted m-5' onClick={() => removeTodo(index)}>X</button>
                                    </div>
                                    : null
                                }
                            </div>
                        </div>
                    ))}
                    {/*makeMenu()*/}
                    <div className='flexContainer'>
                        <div className='button color-green r-10 bg-darker m-5 bold flex3Column p-10 size25 columnCenterAlign' onClick={() => reset()}>
                            RESET
                        </div>
                        <div className='button color-yellow r-10 bg-darker m-5 bold flex3Column p-10 size25 columnCenterAlign' onClick={() => editTodos()}>
                            EDIT
                        </div>
                        <div className='button color-red r-10 bg-darker m-5 bold flex3Column p-10 size25 columnCenterAlign' onClick={() => clear()}>
                            CLEAR
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
