import React, { useEffect, useState } from 'react';

const BuoyData = () => {

  const fetch = require('node-fetch');  // If you're running this in Node.js
  const [swellHeight, setSwellHeight] = useState(null);

  useEffect(() => {
    // Fetch the swell height for the Oceanside buoy
    getBuoyDataForOceanside()
      .then(data => {
        // Assuming the data contains swell height in feet
        setSwellHeight(data.swellHeight);
      })
      .catch(error => {
        console.error('Error fetching buoy data:', error);
      });
  }, []);

  async function getBuoyDataForOceanside() {
    const buoyId = '46224';  // Buoy ID for Oceanside, California
    const apiUrl = `https://api.ndbc.noaa.gov/api/v1/datagetter?station=${buoyId}&datatype=swv&units=english&format=json`;

    try {
      const response = await fetch(apiUrl);
      const data = await response.json();

      // Check if the API request was successful
      if (response.ok) {
        // Assuming the data is an array, and we're interested in the first entry
        const firstEntry = data[0];

        // Extract swell height
        const swellHeight = firstEntry.swv1;

        // Log the swell height
        console.log(`Swell height at Oceanside buoy (Buoy ID ${buoyId}): ${swellHeight} feet`);
      } else {
        console.error(`Error fetching data: ${data.message}`);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  }  

  return (
    <div>
      {swellHeight !== null ? (
        <div>
          {`Swell height at Oceanside buoy (Buoy ID ${buoyId}): ${swellHeight} feet`}
        </div>
      ) : (
        <div>Loading swell height...</div>
      )}
    </div>
  );

};

export default BuoyData;
