import React from 'react';
import ReactDOM from 'react-dom';
import logo from './logo.svg';
import logoRed from './logoRed.png';
import logoYellow from './logoYellow.png';
import logoGreen from './logoGreen.png';
import js from './js.png';
import shakaWhite from './shakaWhite.png';
import shakaBlack from './shakaBlack.png';
import './App.css';

const formatName = (user) => `${user.firstName.toLowerCase()} ${user.lastName.toLowerCase()}`;
const user = { firstName: 'John', lastName: 'Santucci'};
const bigUserName = (<h1>{formatName(user)}</h1>);
let element = (<div></div>);
let timeItIs = [
  "shaka time!!!",
  "get a wave brah!",
  "keep froth alive...",
];
let x = 0;
let interval = 0;
let timeItIsNow = timeItIs[x];
const colors = ["green", "yellow", "red"];
const getColorClass = (color) => "color-" + color;
let color = getColorClass(colors[x]);
const tick = () => {
  element = (
    <div className="time mt-40">
      <div class='color-red'>do you know what time it is?</div><br/><b>{new Date().toLocaleTimeString().replace(" ","").toLocaleLowerCase()}</b><br/><br/><div className={ color }><b>{ timeItIsNow }</b></div>
    </div>
  );

  interval = (interval === 3) ? 0 : (interval+1);
  x = (x === (timeItIs.length-1) && (interval === 0)) ? 0 : ((interval === 0) ? (x+1) : x);
  timeItIsNow = timeItIs[x];
  color = getColorClass(colors[x]);
  ReactDOM.render(element, document.getElementById('time'));
}
setInterval(tick, 1000);

function App() {
  return (
    <div className="App">
      <div className="flexContainer header">
        <div className="flex3Column bg-green" />
        <div className="flex3Column bg-yellow" />
        <div className="flex3Column bg-red" />
      </div>
      <header className="App-header">
        <img src={shakaBlack} className="App-js logo" alt="js" />
        <div className="flexContainer">
          <div className="flex3Column height200" />
          <div className="flex3Column height200">
            <img src={logoRed} className="App-logo1 logo height200 ml-100" alt="logoRed" />
            <img src={logoYellow} className="App-logo2 logo height200 ml-100" alt="logoYellow" />
            <img src={logoGreen} className="App-logo3 logo height200 ml-100" alt="logoGreen" />
          </div>
          <div className="flex3Column height200" />
        </div>
          { bigUserName }
        <a className="App-link"
          href="https://jsongithub.github.io/portfolio/"
          target="_self"
          rel="noopener noreferrer"
        >
          portfolio
        </a>
        <div id="time">{ element }</div>
      </header>
      <div className="flexContainer footer">
        <div className="flex3Column bg-red" />
        <div className="flex3Column bg-yellow" />
        <div className="flex3Column bg-green" />
      </div>
    </div>
  );
}

export default App;
