import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WikiSearch = () => {
    const [term, setTerm] = useState('');
    const [results, setResults] = useState([]);

    //console.log(results);
    //always invoked on mount 
    //and when any second parameter array value changes
    useEffect(() => {
        const search = async () => {
            const { data } = await axios.get('https://en.wikipedia.org/w/api.php', {
                params: {
                    action: 'query',
                    list: 'search',
                    origin: '*',
                    format: 'json',
                    srsearch: term
                }
            });
            setResults(data.query.search);
        };

        if (term && !results.length) { 
            search() 
        } else {
            const timeoutId = setTimeout(() => {
                if (term) {
                    search();
                }
            }, 1000);

            return () => {
                clearTimeout(timeoutId);
            };
        }

    }, [term, results.length]);

    const renderedResults = results.map((result) => {
        return (
            <a 
                href={`https://en.wikipedia.org?curid=${result.pageid}`} 
                className='button white' 
                key={result.pageid}
            >
                <div className='color-yellow bold r-10-t columnLeft bg-green p-10'>{result.title}</div>
                <div className='r-10-b bg-darker p-10 columnLeft mb-5'>
                    <span dangerouslySetInnerHTML={{ __html: result.snippet }}></span>
                </div> 
            </a>
        )
    });

    return (
        <div className="m-20">
            <div className='ui form'>
                <div className='flexContainer bg-dkGreen r-10 p-10'>
                    <label className='color-yellow greet flex2Column10Percent p-10'><span>Search: </span></label>
                    <input 
                        value={term} 
                        onChange={e => setTerm(e.target.value)}
                        className='input flex2Column'
                    />
                </div>
            </div>
            <div className='ui celled list'>{renderedResults}</div>
        </div>
    )
};

export default WikiSearch;
