import React from 'react';
import sequenceImages from './SequenceImages.js';

class PhotoSequence extends React.Component {
    index = 0;
    images = require.context('../../public/images', true);
    constructor(props) {
        super(props);
        this.state = {
            //images: sequenceImages,
            images: ['IMG_2034.JPG',
                'IMG_2049.JPG',
                'IMG_2060.JPG',
                'IMG_2061.JPG',
                'IMG_2062.JPG',
                'IMG_2063.JPG'
            ],
            //url1: `https://lh3.googleusercontent.com/${sequenceImages[0].image}`
            url1: this.images(`./IMG_2034.JPG`)
        }
    }
    componentDidMount() {
        this.timerID = setInterval(
            () => this.getAnImage(),
            7000
        );
    }
    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    getAnImage = () => {
        this.index = (this.index === this.state.images.length-1) ? 0 : this.index+1;
        const i = this.index;
        //let url1 = `https://lh3.googleusercontent.com/${this.state.images[i].image}`;
        let url1 = this.images(`./${this.state.images[i]}`);
        console.log(`getAnImage => imgArray[${i}].image: ${url1}`)
        this.setState({
            url1: url1,
        });
    }

    render() {
        return (
            <div>
                <img id="photo1" className="width-100-percent" src={this.state.url1} alt={this.state.url1} />
            </div>
        )
    }
}

export default PhotoSequence;
