import React from 'react';
/*
<FormRadio 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class FormRadio extends React.Component {

    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            label: props.label,
            groupTitle: props.groupTitle,
            items: props.items
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange = (event) => {
        console.log(`handleChange => ${event.target.value} index: ${event.target.id}`)
        this.setState({
            selected: event.target.value,
            value: event.target.value
        });
    }
    selectItems() {
        return this.state.items.map((item, index) =>
            <div className="flex3Column" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} >
                <input id={index} key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} type="radio" value={item.toString()} onChange={this.handleChange} checked={Number(this.state.selected) === Number(index) ? true : false}/>
                <span className="description" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))}>{item.toString()}</span>
            </div>
        )
    } 
    handleSubmit(event) {
        event.preventDefault();
    };

    render() {
        return (
            <div className="m-5 p-20 m-5 flexContainer">    
                {this.selectItems()}
            </div>
        );
    };
}
export default FormRadio;
