import React from 'react';
//import ReactDOM from 'react-dom';

class Toggle extends React.Component {

  constructor(props) {
    super(props);
    this.isMotionOn = this.props.isMotionOn;
    this.setMotion = this.props.setMotion();
    this.setMotion = this.setMotion.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      isToggleOn: !state.isToggleOn
    }));
    if (this.state.isToggleOn) {
        document.getElementById('shaka').classList.remove('shakingShaka');
    } else {
        document.getElementById('shaka').classList.add('shakingShaka');
    }
  }
  render() {
    return (
      <div className='width-100-percent mt-20 responsiveTopMargin'>
        <span className="greet color-neogreen">Motion</span><br/>
        <button  className="button-green" onClick={this.handleClick}>
          {this.state.isToggleOn ? 'ON' : 'OFF'}
        </button>
      </div>
    );
  }
}
export default Toggle;
