import React from 'react';
import Loader from '../utils/Loader.js';
import TextColorizer from '../utils/TextColorizer.js';
import useCurrentWidth from '../utils/WindowSizeDetection.js';
class Header extends React.Component {

    constructor(props) {
        super(props);
        this.company = this.props.company;
        this.isMotionOn = this.props.isMotionOn; 
        this.setMotion = this.props.setMotion;
    }
    getWidth = () => window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth;

    useCurrentWidth() {
        let width = this.getWidth();
        let setWidth = this.getWidth();
        let timeoutId = null;
        const resizeListener = () => {
            clearTimeout(timeoutId);
            timeoutId = setTimeout(() => setWidth(this.getWidth()), 150);
        };
        window.addEventListener('resize', resizeListener);
        return () => {
            window.removeEventListener('resize', resizeListener);
        }
        return width;
    }


    render() {
        const navClasses = "width-100-percent header mt-2 faded bg-dark";
        const isNavButton = (event) => (event.target.nodeName === "BUTTON") ? true : false;
        const showContent = (event) => (window.location.pathname = event.target.innerHTML);
        const siteNavClick = (event) => (isNavButton(event)) ? (showContent(event)) : false; 
        const navButton = (label) => <button className="navButton buttonPad">{label}</button>;
        const logoButton = (label) => <button className="navButton logoButton">{label}</button>;
        const path = window.location.pathname.toLocaleLowerCase();
        const isHomePage = (path === '/home' || path === '/') ? true : false;
        //console.log(`path: ${path} isHomePage: ${isHomePage}`)

        const Branding = () => {
            if (isHomePage === true) {
                return <div className="pt-70">
                            <Loader isMotionOn={this.props.isMotionOn}/>
                            <TextColorizer class='bigHeader' text={this.company}/>
                            <TextColorizer class='bigHeader' text={useCurrentWidth}/>
                            <TextColorizer class='bigHeader' text={String(this.useCurrentWidth())}/>
                        </div>
            }
            return <div className='mt-88'></div>
        }

        return (
            <div className="App-header">
                <div className="rgb-stripe"></div>
                <div className="flexContainer header">
                    <div className="flex3Column bg-green" />
                    <div className="flex3Column bg-yellow" />
                    <div className="flex3Column bg-red" />
                </div>
                <div className={navClasses} onClick={siteNavClick}>
                    {logoButton(<TextColorizer class='navBranding' text={this.company}/>)}
                    {navButton('Home')}
                    {navButton('BowlBuilder')}
                    {navButton('TempConverter')}
                    {navButton('Essay')}
                    {navButton('Reservation')}
                    {navButton('GuestList')}
                    {navButton('SurfLog')}

                </div>
                <Branding />
            </div>
        );
    };
}

export default Header;
