import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WikiSearch = () => {
    const [term, setTerm] = useState('');
    const [results, setResults] = useState([]);
    //console.log(results);
    //always invoked on mount
    useEffect(() => {
        const search = async () => {
            const { data } = await axios.get('https://en.wikipedia.org/w/api.php', {
                params: {
                    action: 'query',
                    list: 'search',
                    origin: '*',
                    format: 'json',
                    srsearch: term
                }
            });
            setResults(data.query.search);
        };
        const timeoutId = setTimeout(() => {
            if (term) { search() }
        }, 500);        
    }, [term]);

    const renderedResults = results.map((result) => {
        return (
            <div className='item columnLeft' key={result.pageid}>
                <div className='right floated content'>
                    <a 
                        href={`https://en.wikipedia.org?curid=${result.pageid}`} 
                        className='ui button'
                    >
                        more
                    </a>
                </div>
                <div className='content'>
                    <div className='color-yellow bold'>{result.title}</div>
                    <div className='m-10'>
                        <span dangerouslySetInnerHTML={{ __html: result.snippet }}></span>
                    </div>
                </div>
            </div>
        )
    });

    return (
        <div className="m-20">
            <div className='ui form'>
                <div className='field'>
                    <label><span className='color-yellow greet'>Search: </span></label>
                    <input 
                        value={term} 
                        onChange={e => setTerm(e.target.value)}
                        className='input'
                    />
                </div>
            </div>
            <div className='ui celled list white'>{renderedResults}</div>
        </div>
    )
};

export default WikiSearch;
