import React, { useState, useEffect } from 'react';
import menu from '../../assets/images/menuYellow.png';

const ExpenseTracker = () => {

  const currencyOptions = {
    US: 'Dollar',
    Mexico: 'Peso',
    Nicaragua: 'Córdoba',
    'Costa Rica': 'Colones',
    Indonesia: 'Rupiah',
    Australian: 'AUD',
  };

  const currencies = {
    MXN: `Pesos`,   // Mexican Peso
    NIO: `Córdoba`,   // Nicaraguan Cordovas
    CRC: `Colones`,    // Costa Rican Colones
    IDR: `Rupiah`,  // Indonesian Rupiah
    AUD: `AUD`,    // Australian Dollar
  };

  const [exchangeRates, setExchangeRates] = useState({
    USD: 1,
    MXN: 17.16,   // Mexican Peso
    NIO: 36.55,   // Nicaraguan Cordovas
    CRC: 541.23,    // Costa Rican Colones
    IDR: 15045.30,  // Indonesian Rupiah
    AUD: 1.50,    // Australian Dollar
  });

  const [usdAmount, setUsdAmount] = useState('');
  const [selectedCurrency, setSelectedCurrency] = useState('MXN');
  const [convertedAmount, setConvertedAmount] = useState('');
  const [settings, setSettings] = useState(false)






  const [expenses, setExpenses] = useState([]);
  const [expenseData, setExpenseData] = useState({
    date: '',
    time: '',
    expense: '',
    location: '',
    cost: '',
    currency: '',
  });

  useEffect(() => {
    const savedExchangeRates = localStorage.getItem('exchangeRates');
    if (savedExchangeRates) {
      setExchangeRates(JSON.parse(savedExchangeRates));
    }
  }, []);

  useEffect(() => {
    const savedExpenses = localStorage.getItem('expenses');
    if (savedExpenses) {
      setExpenses(JSON.parse(savedExpenses));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('expenses', JSON.stringify(expenses));
  }, [expenses]);

  const handleInputRateChange = (event) => {
    const { name, value } = event.target;
    setExpenseData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleAddExpense = () => {
    const newExpense = { ...expenseData };
    newExpense.date = getCurrentDate();
    newExpense.time = getCurrentTime();
    setExpenses((prevExpenses) => [...prevExpenses, newExpense]);
    setExpenseData({
      date: '',
      time: '',
      expense: '',
      location: '',
      cost: '',
      currency: '',
    });
  };

  const getCurrentDate = () => {
    const currentDate = new Date();
    return currentDate.toDateString();
  };

  const getCurrentTime = () => {
    const currentTime = new Date();
    return currentTime.toLocaleTimeString();
  };
  useEffect(() => {
    localStorage.setItem('exchangeRates', JSON.stringify(exchangeRates));
  }, [exchangeRates]);

  const handleExpenseInputChange = (event) => {
    //alert(`USD: ${event.target.value}`)
    setUsdAmount(event.target.value);
  };
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    alert(`name: ${name}`)
    if (name ==='') {
      handleExpenseInputChange(event);
    }
    setExpenseData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleCurrencyChange = (currency) => {
    setSelectedCurrency(currency);
  };
  const toggleSettings = () => {
    setSettings(!settings)
  }

  const handleRateChange = (rate, value) => {

    setExchangeRates(prevState => ({
      ...prevState,
      [rate]: value
    }));
    toggleSettings();

  }
  const convertToUS = () => {
      const rate = exchangeRates[selectedCurrency];
      const convertedValue = usdAmount / rate;
      setConvertedAmount(convertedValue.toFixed(2));
  };

  return (
    <div>
      <div>
        <img className='color-yellow button' src={menu} alt="open menu" onClick={() => toggleSettings()} />
      </div>
      {settings ? (
        Object.keys(exchangeRates).map(rate => (
          <div key={rate}>
            <label
              className='p-20 m-5 color-lite bold'
              htmlFor={rate}
            >
              {rate}
            </label>
            <input
              className='p-20 m-5 color-lite bold bg-black'
              type="number"
              id={rate}
              value={exchangeRates[rate]}
              onChange={e => handleRateChange(rate, e.target.value)}
            />
          </div>
        ))
      ) : (
        <div></div>
      )}
      <div className='bg-darker r-10 p-10 ml-20 mr-20 mb-1 bold'>
        <label>
          Expense:
          <input
            type="text"
            name="expense"
            value={expenseData.expense}
            onChange={handleInputChange}
            className='r-10 p-10 mr-20 ml-20 bold'
          />
        </label>
      </div>
      <br />
      <div className='bg-darker r-10 p-10 ml-20 mr-20 mb-1 bold'>
        <label>
          Location:
          <select
            name="location"
            value={expenseData.location}
            onChange={handleInputChange}
            className='r-10 p-10 mr-20 ml-20 bold'
          >
            <option value="">Select Location</option>
            {Object.keys(currencyOptions).map((location) => (
              <option key={location} value={location}>
                {location}
              </option>
            ))}
          </select>
        </label>
      </div>
      <br />
      <div className='bg-darker r-10 p-10 ml-20 mr-20 mb-1 bold'>
        <label>
          Cost:
          <input
            type="number"
            name="cost"
            value={expenseData.cost}
            onChange={handleInputChange}
            className='r-10 p-10 mr-20 ml-20 bold'
          />
        </label>
      </div>
      <br />
      <div className='bg-darker r-10 p-10 ml-20 mr-20 mb-1 bold'>
        <label>
          Currency:
          <select
            name="currency"
            value={expenseData.currency}
            onChange={handleInputChange}
            className='r-10 p-10 mr-20 ml-20 bold'
          >
            <option value="">Select Currency</option>
            {Object.values(currencyOptions).map((currency) => (
              <option key={currency} value={currency}>
                {currency}
              </option>
            ))}
          </select>
        </label>
      </div>
      <br />
      <div>
        <div className='bg-orange greet timerBox m-20 color-black' onClick={handleAddExpense}>Add Expense</div>
      </div>
      <br />
      <br />
      <div className='bg-darker r-10 p-10 ml-20 mr-20 mb-1 bold'>
        <h3>Expense List</h3>
        {expenses.length === 0 ? (
          <p>No expenses recorded.</p>
        ) : (
          <ol>
            {expenses.map((expense, index) => (
              <li className='p-10 ml-20 mr-20 mb-1 bold lowerBorder size20' key={index}>
                {expense.expense}: {'USD'}
                <div className='copyright'>
                  {expense.date} - {expense.time} - {expense.location} - ${expense.cost} {expense.currency}s
                </div>
              </li>
            ))}
          </ol>
        )}
      </div>
    </div>
  );
};

export default ExpenseTracker;
