import React, { useState, useEffect } from 'react';
import LongPressButton from './LongPressButton.js';
import Selector from '../forms/FunctionalSelector.js';
import getKey from '../utils/KeyGenerator.js';
import ScoreCard from './ScoreCard.js';

const SurfScores = ({ players, editPlayer, deletePlayer, winner, updateTwoPlayerScores, game, surfKey, time, setTime, heatLength, isWinner, getTwoHighestScores}) => {

    const [timerOn, setTimerOn] = useState(false);
    const [timesUp, setTimesUp] = useState(false);

    useEffect(() => {
        let intervalId;
        if (timerOn) {
            intervalId = setInterval(() => {
                setTime((prevTime) => {
                    const newTime = prevTime - 1;
                    if (newTime <= 0) {
                        clearInterval(intervalId);
                        setTimesUp(true);
                        return 0;
                    }
                    return newTime;
                });
            }, 1000);
        }

        return () => clearInterval(intervalId);
    }, [timerOn]);

    useEffect(() => {
        if (timesUp) {
            setTimerOn(false);
            setTime(heatLength);
            console.log(`heatLength: ${heatLength}`)
            setTimesUp(false);
        }
    }, [timesUp]);

    const toggleTimer = () => {
        if (timerOn) {
            setTimerOn(false)
        } else {
            setTimerOn(true)
        }
    }

    const findTwoHighestIndices = (scores) => {
        let highestIndex = 0;
        let secondHighestIndex = -1;

        scores.forEach((score, index) => {
            if (score > scores[highestIndex]) {
                secondHighestIndex = highestIndex;
                highestIndex = index;
            } else if (secondHighestIndex === -1 || score > scores[secondHighestIndex]) {
                secondHighestIndex = index;
            }
        });

        return [highestIndex, secondHighestIndex];
    };

    const scorecard = (player, playerIndex, scoreIndex, score) => {

        //console.log(`${game} - scorecard => player:${player} playerIndex: ${playerIndex} scoreIndex: ${scoreIndex} score: ${score}`)
        return <ScoreCard
            game={game}
            players={players}
            playerIndex={playerIndex}
            scoreIndex={scoreIndex}
            editPlayer={editPlayer}
            deletePlayer={deletePlayer}
            updateTwoPlayerScores={updateTwoPlayerScores}
            winner={winner}
            key={getKey(player)}
        />

    }

    const surfClass = (playerIndex) => 'flex3Column p-20 size25 bold' + ((isWinner(playerIndex)) ? ' color-neogreen shakingShaka mt-20' : '');

    const getTimerButtonClasses = () => {
        const timerButtonClasses = timerOn ? 'bg-red m-10 r-10 bold size20 p-20 color-light' : 'bg-green m-10 r-10 bold size20 p-20 color-light';
        return timerButtonClasses;
    }
    const getTimerClasses = () => {
        const timerClasses = (time < 120) ? 'blinking-fade' : '';
        return timerClasses;
    }
    const formatTime = (timeInSeconds) => {
        const hours = Math.floor(timeInSeconds / 3600);
        const minutes = Math.floor((timeInSeconds % 3600) / 60);
        const seconds = timeInSeconds % 60;

        const formattedHours = hours.toString().padStart(2, '0');
        const formattedMinutes = minutes.toString().padStart(2, '0');
        const formattedSeconds = seconds.toString().padStart(2, '0');

        return `${formattedHours}:${formattedMinutes}:${formattedSeconds}`;
    };

    return <React.Fragment>
                <div className={getTimerButtonClasses()} onClick={() => toggleTimer()}>
                    {(timerOn) ? 'STOP' : 'START'}
                </div>
                <div className='bg-dkBlue m-10 r-10 bold size20 p-20 color-light0'><span className={getTimerClasses()}>{formatTime(time)}</span></div>
                <div>
                    <div className='flexContainer color-yellow bold greet ml-10 mr-10'>
                        <div className='ml-10 p-10 w-100 bg-dkGreen m-1 r-5-lft'>Rank</div>
                        <div className='flex3ColumnLeft m-1 p-10 bg-dkGreen m-1'>Athlete</div>
                        <div className='flex3ColumnRight mr-10 p-10 bg-dkGreen m-1 r-5-rt'>Total</div>
                    </div>
                </div>
            </React.Fragment>

}

export default SurfScores;
