import React from 'react';

class PhotoBlog extends React.Component {
    index = 0;
    constructor(props) {
        super(props);
        const images = [
            { "image": "Screen Shot 2020-07-13 at 11.52.15 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.53.06 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.53.24 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.53.43 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.54.07 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.54.23 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.54.43 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.54.55 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.55.09 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.55.17 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.55.34 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.55.45 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.56.02 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.56.17 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.56.30 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.56.38 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.56.56 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.57.08 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.57.22 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.57.32 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.57.44 PM.png" },
            { "image": "Screen Shot 2020-07-13 at 11.57.54 PM.png" }
        ];
        this.state = {
            images: images,
            url1: images[0].image
        }
    }
    componentDidMount() {
        this.timerID = setInterval(
            () => this.getAnImage(),
            700
        );
    }
    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    getAnImage = () => {
        this.index = (this.index === this.state.images.length-1) ? 1 : this.index+1;
        const i = this.index;
        console.log(`getAnImage => imgArray[${i}].image: ${this.state.images[i].image}`)
        let url1 = `../../assets/photos/${this.state.images[i].image}`;
        this.setState({
            url1: url1,
        });
    }

    render() {
        return (
            <div>
                <img id="photo1" className="width-100-percent" src={this.state.url1} alt={this.state.url1} />
            </div>
        )
    }
}

export default PhotoBlog;
