import React from 'react';

class SlideShow extends React.Component {
    slideShow = true;
    constructor(props) {
        super(props);
        this.toggleSlideShow = this.toggleSlideShow.bind(this);
    }
    getAnImage = (i, imgArray) => {
        console.log(`getImageSlideShow => ${i}`)
        let url1 = imgArray[i].image;
        let url2 = imgArray[i+30].image;
        let url3 = imgArray[i+60].image;
        //console.log(url1);
        //console.log(url2);
        //console.log(url3);
        this.setState({
            url1: url1,
            url2: url2,
            url3: url3
        });
    }
    toggleSlideShow = () => {
        this.slideShow = !this.slideShow;
        this.getAnImage(this.index)
    }
    getImageSlideShow = (css, path, imgArray, index) => {
        index++
        if (index >= imgArray.length/3) { index = 1; }
        setTimeout(() => this.getAnImage(css, path, imgArray, index), (1000))
    }
    images = [];
    init = () => {
        let x=1;
        for(let x=1;x<=90;x++) {
            if (x<31) {
                this.images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/socal/local/socal_large_"+x+".png" })
            } else if (x < 61) {
                this.images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/nbaja/puntabaja/nearshore/puntabaja_large_"+(x-30)+".png" })
            } else {
                this.images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/sbaja/local/sbaja_large_"+(x-60)+".png" })
            }
        }
        this.getImageSlideShow("swell", "", this.images, 0)
    }
    render() {

        if (this.slideShow === true) {
            //app.getImageSlideShow("mobileImage", "", imgArray, index++)
        }

        return (
            <div>
                <img id="slideshow1" src={this.state.url1} onclick={() => this.toggleSlideShow()} alt="California Sur Swell" />
                <br/>
                <img id="slideshow1" src={this.state.url2} onclick={() => this.toggleSlideShow()} alt="Baja Norte Swell" />
                <br/>
                <img id="slideshow1" src={this.state.url3} onclick={() => this.toggleSlideShow()} alt="Baja Sur Swell" />
            </div>

        )
    }
}

export default SlideShow;
