import React from 'react';
import Dialog from './functional/Dialog.js';

class LogEntry extends React.Component {

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSignUp = this.handleSubmit.bind(this);
        this.state = {
            title: props.title,
            message: props.message,
            items: props.items,
            buttonLabel: props.buttonLabel,
//            types: props.items.map((item) => item.type),
//            descriptions: props.items.map((item) => item.description),
            selections: props.items.map((item) => item.selections),
            selectItems: props.items[0].selections.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>
            )
        };
        //console.log(this.state.item1Selections[17]);
    }
    //descriptions = this.state.descriptions;
    //types = this.state.types;
    selections = this.state.selections;

/*
    <form onSubmit={this.handleSubmit} className="neumorphism p-20">
        {this.state.header}<br/><br/>
        <textarea id="ingredients" rows="11" cols="10" className="mb-20" value={this.state.bowlIngredients} /><br/>
        <label>
            <select value={this.state.value} onChange={this.handleChange}>
                {this.state.selectItems}
            </select>
        </label><br/>
        <input type="submit" value="Add Item" onClick={this.add}/>
        <input type="submit" value="Remove Item" onClick={this.remove} className="ml-2"/>
    </form>
*/
    handleSelection(e) {
        console.log(`Selected: ${e.target.value}`);
    }
    handleChange(e) {
        this.setState({login: e.target.value});
    }
    handleSubmit() {
        alert(`Welcome aboard, ${this.state.login}!`);
    }
    /*
    item1Type: props.items[0].type,
    item1Description: props.items[0].description,
    item1Selections: props.items[0].selections,
    item1Selection1: props.items[0].selections[0],
    buttonLabel: props.buttonLabel,
    selectItems: props.items[0].selections.map((item) =>
        <option 
            key={item.toString().toLowerCase()} 
            value={item.toString()}>{item}
        </option>
    )
    */
    defaultSelection = (item) => Math.round(this.state.selections[item].length/2);
    selector = (item) => <select value={this.state.selections[item][this.defaultSelection(item)]} onChange={this.handleSelection}>
                                {this.state.selectItems}
                            </select>;
    /*
    selectItems = (item) => item.selections.map((selection) => 
        <option 
            key={selection.toString().toLowerCase()} 
            value={selection.toString()}>{selection}
        </option>
    )
    */
    buildSelectors = () => this.state.items.map((item) =>
        <select value={item.selections[0]} onChange={this.handleSelection}>
            {this.state.selectItems(item)}
        </select>
    )
    render() {
        //let inputField = (this.state.items[item].type === "selector") ? selector : false;
        //let defaultSelection = Math.round(this.state.items[item].selections.length/2);
        /*const selector = <select value={this.state.items[item].selections[defaultSelection]} onChange={this.handleSelection}>
                                {this.state.selectItems}
                            </select>;
        */

        return (
            <Dialog title={this.state.title} message={this.state.message}>
                <input value={this.state.entry} onChange={this.handleChange} /><br/><br/>
                {/*{this.state.item1Description}<br/>*/}
                {/*this.state.descriptions[0]}<br/>*/}
                {/*{selector}*/}
                {this.buildSelectors}
                <button onClick={this.handleSubmit}>
                    {this.state.buttonLabel}
                </button>
            </Dialog>
        );
    }
}

export default LogEntry;
