import React from 'react';
import SearchBar from './utils/SearchBar';
import YouTube from './utils/YouTube';
import VideoList from './utils/VideoList';
import VideoDetail from './utils/VideoDetail'

class Videos extends React.Component {

  state = { 
    videos: [],
    KEY: 'AIzaSyDxITGx4VJUa4_nUnhJJqwa790l5ICifNU',
    api: 'https://www.googleapis.com/youtube/v3',
    selectedVideo: null
  };

  onTermSubmit = async term => {
    console.log(`onTermSubmit =====> ${term}`);
    const response = await YouTube.get("/search", {
      params: {
        q: term,
        part: "snippet",
        maxResults: 5,
        type: 'video',
        key: this.state.KEY
      }
    });
    this.setState({ videos: response.data.items});
    const videos = [
      {
        title: 'cars',
        video: ''
      },
      {
        title: 'monkeys',
        video: ''
      }
    ]
    //this.setState({ videos: videos});
  };
  onVideoSelect = (video) => {
    console.log(`video: ${JSON.stringify(video, null, 2)}`)
    this.setState({ selectedVideo: video })
  }

  render() {
      return (
          <div className='flexContainer mt-10'>
              <SearchBar onSubmit={this.onTermSubmit} KEY={this.state.KEY} api={this.state.api} />
              <div className='flexThreeFourthColumn'>
                <VideoDetail video={this.state.selectedVideo} />
              </div>
              <div className='flexOneFourthColumn'>
                <VideoList onVideoSelect={this.onVideoSelect} videos={this.state.videos} />
              </div>
          </div>
      )
  }
}
export default Videos;
