import React from 'react';
import Dialog from './functional/Dialog.js';
import shakaBlack from './assets/images/shakaBlack.png';
import thumbsUp from './assets/images/ThumbsUp.png';
import thumbsDown from './assets/images/ThumbsDown.png';
import Selector from './Forms/FunctionalSelector.js';
import RadioSelector from './Forms/FormRadio.js';
import DateTimePicker from 'react-datetime-picker';
import DatePicker from 'react-date-picker';

class LogEntry extends React.Component {

    constructor(props) {
        super(props);
        this.handleSelection = this.handleSelection.bind(this);
        this.updateNotes = this.updateNotes.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onDateChange = this.onDateChange.bind(this);
        this.state = {
            date: new Date(),
            title: props.title,
            message: props.message,
            items: props.items,
            buttonLabel: props.buttonLabel
        };
    }

    handleSelection(groupTitle, label, selected) {
        console.log(`${groupTitle} => ${label} -> ${selected}`);
        let subStateObject = {};
        subStateObject[label] = selected;
        let stateObject = {};
        stateObject[groupTitle] = subStateObject;
        console.log(JSON.stringify(stateObject))
        /*
        this.setState(
            stateObject
        );
        */
    }
    updateNotes(event) {
        this.setState({notes: event.target.value});
    }
    handleSubmit(event) {
        alert(`Your Welcome! Check console...`);
        console.log(JSON.stringify(this.state));
        event.preventDefault();
    }
    onDateChange(date) {
        const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thurdsday", "Friday", "Saturday"]
        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
        const hours = (hours) => (hours>12) ? (hours-12) : hours;
        const timeOfDay = (hours) => (hours>12) ? "PM" : "AM";
        const month = (date !== null) ? date.getMonth()+1 : false;
        const day = (date !== null) ? date.getDate() : false;
        const year = (date !== null) ? date.getFullYear() : false;
        const minutes = (minutesIn) => (minutesIn<10) ? (0+String(minutesIn)) : minutesIn;
        const time = (date !== null) ? `${hours(date.getHours())}:${minutes(date.getMinutes())}${timeOfDay(date.getHours())}` : false;
        //this.setState({ date: date });
        //console.log(`${JSON.stringify(date)}`)
        //console.log(`${days[date.getDay()]}`)
        //console.log(`${months[date.getMonth()]}`)
        //console.log(`${date.getDate()}`)
        //console.log(`${date.getFullYear()}`)
        //console.log(`${hours(date.getHours())}:${date.getMinutes()}${timeOfDay(date.getHours())}`)
        //console.log(`${date.getMonth()+1}/${date.getDate()}/${date.getFullYear()}`)
        this.setState({
            month: month,
            day: day,
            year: year,
            //time: time
        })
    }
    //////
    items = () => this.state.items;
    group = (item) => item.group;
    selectionInterface = (item, groupTitle) => (item.type === "radio") ? radio(item, groupTitle) : selector(item, groupTitle);
    groups = () => this.items().map((item) => {
        const getKey = () => item.description.toString().toUpperCase()+(Math.round(Math.random()*100));
        const headerClasses = 'subHeader color-yellow';
        const selectorClasses = "greet p-20 bg-vdk-green flex3Column";
        const groupClasses = "flexContainer radius-10";
        const description = item.description;
        return <div key={getKey()}>
                    <div key={getKey()} className={headerClasses}>
                        {item.description}
                    </div>
                    <div className={groupClasses} key={getKey()}>
                        {this.group(item).map((group) => 
                            <div key={getKey()} className={selectorClasses}>
                                {this.selectionInterface(group, description)}
                            </div>
                        )}
                    </div>
                </div>
    })
    categories = () => <div className='description'>{this.groups()}</div>;
    dateEntry = () => <div>
                        <div className='mb-5 subHeader color-yellow'>Date</div>
                        <div className='flexContainer bg-vdk-green'>
                            <DatePicker
                                onChange={this.onDateChange}
                                value={this.state.date} 
                                className='p-10 bg-green flex3Column radius-10 m-20'
                            /><br/>
                        </div>
                    </div>

    render() {

        const defaultSelection = (item) => Math.round(item.selections.length/2); 
        const radioItems = (item, groupTitle) => <RadioSelector
                    header={groupTitle}
                    groupTitle={groupTitle} 
                    selected={Math.floor(item.selections.length/2)} 
                    label={item.description} 
                    items={item.selections} 
                    onChange={this.handleSelection}
                />;

        const selector = (item, groupTitle) => <div className="radius-10 p-20 bg-green">
                    <Selector 
                        groupTitle={groupTitle} 
                        selected={defaultSelection(item)} 
                        label={item.description} 
                        items={item.selections}
                        onChange={this.handleSelection}
                    />
                </div>;

        const radio = (item, groupTitle) => <div className="radius-10 bg-green">
                    {radioItems(item, groupTitle)}
                </div>;


        return (
            <Dialog title={this.state.title} message={this.state.message}>
                <form onSubmit={this.handleSubmit}>
                    {this.dateEntry()}
                    {this.categories()}
                    <br/>
                    <textarea value={this.state.entry} onChange={this.updateNotes} /><br/><br/>
                    <button onClick={this.handleSubmit}>
                        {this.state.buttonLabel}
                    </button>
                </form>
            </Dialog>
        );
    };
}

export default LogEntry;
