import React from 'react';
import Loader from '../utils/Loader.js';
import TextColorizer from '../utils/TextColorizer.js';
import menu from '../../assets/images/menuYellow.png';
import close from '../../assets/images/menuClose.png';

class Header extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            menu: false,
            initialized: false
        };
        this.company = this.props.company;
        this.isMotionOn = this.props.isMotionOn; 
        this.setMotion = this.props.setMotion;
    }
    navClassesClosed = "width-100-percent header mt-2 faded bg-dark";
    navClassesOpen = "width-100-percent header mt-2 fadeInFaded fadedDark bg-dark";
    navClassesClose = "width-100-percent header mt-2 fadeOutFaded faded bg-dark";
    render() {
        const isWideScreen = (this.props.width >= 1080) ? true : false;

        const navClasses = (this.state.menu) ? this.navClassesOpen : (this.state.initialized) ? this.navClassesClose : this.navClassesClosed;
        const isNavButton = (event) => (event.target.nodeName === "BUTTON") ? true : false;
        const showContent = (event) => (window.location.pathname = event.target.innerHTML);
        const siteNavClick = (event) => (isNavButton(event)) ? (showContent(event)) : menuClick(); 
        const navButton = (label) => <button className="navButton buttonPad">{label}</button>;
        const logoButton = (label) => <button className="navButton logoButton">{label}</button>;
        const closeButton = <button className="navButton menuPad"><img src={close} alt="close menu" /></button>
        const burgerButton = <button className="navButton menuPad"><img src={menu} alt="open menu" /></button>;
        const getMenuButton = (this.state.menu) ? closeButton : burgerButton;
        const path = window.location.pathname.toLocaleLowerCase();
        const isHomePage = (path === '/home' || path === '/') ? true : false;
        //console.log(`path: ${path} isHomePage: ${isHomePage}`)

        const Branding = () => {
            if (isHomePage === true) {
                return <div className="pt-70">
                            <Loader isMotionOn={this.props.isMotionOn}/>
                            <TextColorizer class='bigHeader' text={this.company}/>
                        </div>
            }
            return <div className='mt-88'></div>
        };
        const backgroundClass = (this.props.isMotionOn) ? 'rgb-stripe' : 'rgb-stripeStopped';
        const menuClick = () => this.setState({
            menu: !this.state.menu,
            initialized: true
        })
        const Background = () => {
             return <div className={backgroundClass}></div>;
        }
        const navItems = [
            'Home',
            'BowlBuilder',
            'TempConverter',
            'Essay',
            'Reservation',
            'GuestList',
            'SurfLog'
        ]
        const wideNav = (item) => navButton(item);
        const portraitNav = (item) => <div>{navButton(item)}</div>;
        const getNavButton = (item) => (isWideScreen) ? wideNav(item) : portraitNav(item);
        const getMenuItems = () => {
            const menuItems = navItems.map((item) =>
                getNavButton(item)
            );

            return (
                menuItems
            );
        }
        const navigation =  <div>
                                {logoButton(<TextColorizer class='navBranding' text={this.company}/>)}
                                {getMenuItems()}
                            </div>
        const hamburgerOpen = <div className="flexContainer">
                                <div className="flex3ColumnLeft">{logoButton(<TextColorizer class='navBranding mt-7' text={this.company}/>)}</div>
                                <div className="flex3Column">
                                    <br/><br/>
                                    {getMenuItems()}
                                </div>
                                <div className="flex3ColumnRight">
                                    {getMenuButton}
                                </div>
                            </div>
        const hamburgerClosed = <div className="flexContainer">
                            <div className="flex3ColumnLeft">{logoButton(<TextColorizer class='navBranding mt-7' text={this.company}/>)}</div>
                            <div className="flex3Column bg-dark"></div>
                            <div className="flex3ColumnRight">
                                {burgerButton}
                            </div>
                        </div>

        const getNavigation = (isWideScreen) ? navigation : (this.state.menu) ? hamburgerOpen : hamburgerClosed;

        return (
            <div className="App-header">
                <Background />
                <div className="flexContainer header">
                    <div className="flex3Column bg-green" />
                    <div className="flex3Column bg-yellow" />
                    <div className="flex3Column bg-red" />
                </div>
                <div className={navClasses} onClick={siteNavClick}>
                   {getNavigation}
                </div>
                <Branding />
            </div>
        );
    };
}

export default Header;
