import React from 'react';
import Dialog from './functional/Dialog.js';

class LogEntry extends React.Component {

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSignUp = this.handleSubmit.bind(this);
        this.state = {
            title: props.title,
            message: props.message,
            items: props.items,
            buttonLabel: props.buttonLabel
            /*
            selectItems: props.items[0].selections.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>
            )
            */
        };
    }

    handleSelection(value, description) {
        console.log(`Selected: ${value} Description: ${description}`);
    }
    handleChange(e) {
        this.setState({login: e.target.value});
    }
    handleSubmit() {
        alert(`Welcome aboard, ${this.state.login}!`);
    }


    render() {
        let id = 0;
        const selectId = () => this.id = this.id + 1;
        const items = this.state.items;
        const defaultSelection = (item) => Math.round(item.selections.length/2);        
        const selectItems = (item) => item.selections.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>
        )
        console.log(`id: ${selectId()}`);
        const selector = (item) => <div className="mb-30">
                                        <p>
                                            {item.description}<br/>
                                            <select value={item.selections[defaultSelection(item)]} onChange={this.handleSelection(this.value, item.description)}>
                                                {selectItems(item)}
                                            </select>
                                        </p>
                                    </div>;

        const fields = () => items.map((item) => selector(item))

        return (
            <Dialog title={this.state.title} message={this.state.message}>
                {fields()}
                <textarea value={this.state.entry} onChange={this.handleChange} /><br/><br/>
                <button onClick={this.handleSubmit}>
                    {this.state.buttonLabel}
                </button>
            </Dialog>
        );
    }
}

export default LogEntry;
