import React from 'react';
import Loader from '../utils/Loader.js';
import TextColorizer from '../utils/TextColorizer.js';
import js from '../../assets/images/js.png';

class Header extends React.Component {

    constructor(props) {
        super(props);
        this.state = {width: window.innerWidth};
        this.company = this.props.company;
        this.isMotionOn = this.props.isMotionOn; 
        this.setMotion = this.props.setMotion;
        this.setIt = this.setIt.bind(this);
    }
    setIt = () => (window.innerWidth !== this.state.width) ? this.setState({ width: window.innerWidth}) : false;
    render() {
        const debounce = (func, wait, immediate) => {
            var timeout;
            return function() {
                var context = this, args = arguments;
                var later = function() {
                    timeout = null;
                    if (!immediate) func.apply(context, args);
                };
                var callNow = immediate && !timeout;
                clearTimeout(timeout);
                timeout = setTimeout(later, wait);
                if (callNow) func.apply(context, args);
            };
        };
        const myEfficientFn = debounce(this.setIt, 250);
        window.addEventListener('resize', myEfficientFn);
        const navClasses = "width-100-percent header mt-2 faded bg-dark";
        const isNavButton = (event) => (event.target.nodeName === "BUTTON") ? true : false;
        const showContent = (event) => (window.location.pathname = event.target.innerHTML);
        const siteNavClick = (event) => (isNavButton(event)) ? (showContent(event)) : false; 
        const navButton = (label) => <button className="navButton buttonPad">{label}</button>;
        const logoButton = (label) => <button className="navButton logoButton">{label}</button>;
        const burgerButton = <button className="navButton buttonPad"><img className="mb-1" src={js} alt="js" /></button>;
        const path = window.location.pathname.toLocaleLowerCase();
        const isHomePage = (path === '/home' || path === '/') ? true : false;
        //console.log(`path: ${path} isHomePage: ${isHomePage}`)

        const Branding = () => {
            if (isHomePage === true) {
                return <div className="pt-70">
                            <Loader isMotionOn={this.props.isMotionOn}/>
                            <TextColorizer class='bigHeader' text={this.company}/>
                        </div>
            }
            return <div className='mt-88'></div>
        };
        const backgroundClass = (this.props.isMotionOn) ? 'rgb-stripe' : 'rgb-stripeStopped';
        const Background = () => {
             return <div className={backgroundClass}></div>;
        }
        const navigation =  <div>
                                {logoButton(<TextColorizer class='navBranding' text={this.company}/>)}
                                {navButton('Home')}
                                {navButton('BowlBuilder')}
                                {navButton('TempConverter')}
                                {navButton('Essay')}
                                {navButton('Reservation')}
                                {navButton('GuestList')}
                                {navButton('SurfLog')}
                            </div>
        const hamburger = <div className="flexContainer header">
                                <div className="flex3Column bg-green">{logoButton(<TextColorizer class='navBranding' text={this.company}/>)}</div>
                                <div className="flex3Column bg-yellow"></div>
                                <div className="flex3Column bg-red">{burgerButton}</div>
                            </div>
        const getNavigation = (this.state.width >= 1080) ? navigation : hamburger;

        return (
            <div className="App-header">
                <Background />
                <div className="flexContainer header">
                    <div className="flex3Column bg-green" />
                    <div className="flex3Column bg-yellow" />
                    <div className="flex3Column bg-red" />
                </div>
                <div className={navClasses} onClick={siteNavClick}>
                   {getNavigation}
                </div>
                <Branding />
            </div>
        );
    };
}

export default Header;
