import React, { useState, useEffect } from 'react';
const GallonsCalculator = () => {
  const [exchangeRate, setExchangeRate] = useState(0);
  const [pricePerLiter, setPricePerLiter] = useState(0);
  const [pricePerLiterUSD, setPricePerLiterUSD] = useState(0);
  const [litersPurchased, setLitersPurchased] = useState(0);
  const [gallons, setGallons] = useState(0);
  const [usdPerGallon, setUsdPerGallon] = useState(0);
  const [totalUSD, setTotalUSD] = useState(0);
  const [totalPesos, setTotalPesos] = useState(0);

  useEffect(() => {
    const newPricePerLiter = (pricePerLiter / exchangeRate)
    setPricePerLiterUSD(newPricePerLiter);
  }, [pricePerLiter, exchangeRate]);

  useEffect(() => {
    const newGallons = litersPurchased * 0.264172;
    setGallons(newGallons.toFixed(2));
  }, [litersPurchased]);

  useEffect(() => {
    const newUsdPerGallon = pricePerLiterUSD * 3.78541;
    setUsdPerGallon(newUsdPerGallon.toFixed(2));
  }, [pricePerLiterUSD]);

  useEffect(() => {
    const newTotalUSD = (gallons * usdPerGallon);
    if (newTotalUSD) {
      setTotalUSD(newTotalUSD.toFixed(2));
    }
  }, [gallons, usdPerGallon]);

  useEffect(() => {
    const newTotalPesos = (litersPurchased * pricePerLiter);
    setTotalPesos(newTotalPesos.toFixed(2));
  }, [litersPurchased, pricePerLiter]);

  return (
    <div>
      <h1>Gallons Calculator</h1>
      <div>
        <label>
          Exchange Rate (foreign currency to USD):
          <input
            type="number"
            value={exchangeRate}
            onChange={(e) => setExchangeRate(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Price per Liter (in foreign currency):
          <input
            type="number"
            value={pricePerLiter}
            onChange={(e) => setPricePerLiter(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Liters Purchased:
          <input
            type="number"
            value={litersPurchased}
            onChange={(e) => setLitersPurchased(e.target.value)}
          />
        </label>
      </div>
      <div>Gallons purchased:</div>
      <div>{gallons}</div>
      <div>USD per gallon:</div>
      <div>${usdPerGallon}</div>
      <div>total dollars:</div>
      <div>${totalUSD}</div>
      <div>total pesos:</div>
      <div>${totalPesos}</div>
    </div>
  );
};

export default GallonsCalculator;
