import React from 'react';
import Dialog from '../functional/Dialog.js';
import Loader from '../utils/Loader.js';

class Tide extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tide: "low",
            height: "1'"
        }
    }
    componentDidMount() {

        let data;
        const returnJSON = (response) => response.json();
        const returnRejection = (response) => Promise.reject({status: response.status, data});
        const validate = (response) => (response.ok) ? returnJSON(response) : returnRejection(response);
        const uri = 'https://tidesandcurrents.noaa.gov/api/datagetter?begin_date=20130101%2010:00&end_date=20130101%2010:24&station=8454000&product=water_level&datum=mllw&units=metric&time_zone=gmt&application=web_services&format=json';
        //const waterTempuri = 'https://tidesandcurrents.noaa.gov/api/datagetter?begin_date=20130808%2015:00&end_date=20130808%2015:06&station=8454000&product=water_temperature&units=english&time_zone=gmt&application=ports_screen&format=json'

        fetch(uri)
            .then(response => validate(response))
            .then(data => {
                this.setState({
                    tide: "HIGH",
                    height: "6'"
                })
            })
            .catch(err => console.log(`Something went wrong!\nuri: ${uri} \npath: ${window.location.pathname}\n`, err));
    }
    getCurrentTide = () => <div>Tide: {this.state.tide} Height: {this.state.height}</div>;

    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    percent = 'twentyfivePercent mt--70 mb--70';
    loading = () => <div className={this.percent}>
                <Loader isMotionOn={this.props.isMotionOn}/>
            </div>;
    render() {
        return <div className="color-yellow greet">
                {this.loading()}
                {this.getCurrentTide()}
            </div>
    };
}

export default Tide;
