import React, {useState, useEffect} from 'react';
import LogEntry from './LogEntryFunctional.js';
import Loader from '../utils/Loader.js';
import LogId from './LogId.js';
import interfaceData from './InterfaceData.js'
import templateData from './TemplateData.js';
const Logger = ({logId, location}) => {

    const logIdComponent = new LogId(logId);
    console.log(`Logger =>\nlogIdComponent.getLogId(): ${logIdComponent.getLogId()}`)
    const logExists = () => (localStorage.getItem(logIdComponent.getLogId()) === null) ? false : true;
    const initLogId = () => (logExists()) ? logIdComponent.getLogId() : logIdComponent.generateNewLogId();
    const initLog = () => (logExists()) ? JSON.parse(localStorage.getItem(logIdComponent.getLogId())) : templateData;
    const [status, setStatus] = useState({
        logID: initLogId(),
        log: initLog(),
        isLoaded: false,
        items: []
    })
    const getSpot = () => localStorage.getItem("spot");
    useEffect(() => {
        const { state } = location;
        const logId = (state === undefined) ? logIdComponent.getLogId() : state.logId.item;
        console.log(`LoggerFunctional => componentDidMount -> SPOT: ${getSpot()}\ngetLogId: ${logIdComponent.getLogId()}\nlogId: ${logId}`); 

        if (localStorage.getItem(logId) === null) {
            setStatus(prevState => ({
                ...prevState,
                log: templateData,
                logID:logIdComponent.generateNewLogId()
            }));
            console.log(`LoggerFunctional => componentDidMount -> 1\nSPOT: ${getSpot()}\nlogID: ${status.logID}\nlog: ${JSON.stringify(status.log, null, 2)}`); 

        } else {
            setStatus(prevState => ({
                ...prevState,
                log: JSON.parse(localStorage.getItem(logId)),
                logID: logIdComponent.getLogId()
            }));
            console.log(`LoggerFunctional => componentDidMount -> 2\nSPOT: ${getSpot()}\nlogID: ${logId}\nlog: ${JSON.stringify(status.log, null, 2)}`); 
        }

        let data = interfaceData;
        const returnJSON = (response) => response.json();
        const returnRejection = (response) => Promise.reject({status: response.status, data});
        const validate = (response) => (response.ok) ? returnJSON(response) : returnRejection(response);
        /*
        const requestInit = {
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            cache: 'default'
        };
        */
        //const uri = new Request('https://jsongithub.github.io/portfolio/assets/data/appData.json', requestInit);
        //GOOD const uri = 'https://jsongithub.github.io/portfolio/assets/data/appData.json';
        const uri = 'https://jsongithub.github.io/portfolio/assets/data/appData.json';
        //const uri = 'localhost:8080/writeSurfLog.json';
        /*
        fetch(uri)
            .then(response => validate(response))
            .then(data => {
                setIsLoaded(true);
                setItems(data);
                setLogID(logId);
            })
            .catch(err => console.log(`Something went wrong!\nuri: ${uri} \npath: ${window.location.pathname}\n`, err));
        */
        setStatus({
            log: JSON.parse(localStorage.getItem(logIdComponent.getLogId())),
            logID: logId,
            items: data,
            isLoaded: true
        });
    }, [location])

    const updateLog = (groupTitle, label, selected, set) => {
        const log = status.log;
        log[groupTitle][label] = selected;
        if (groupTitle !== undefined && groupTitle !== 1 && selected !== undefined && set) {
            setStatus(prevState => ({
                ...prevState,
                log: log
            }));
        }
    }
    const getStateLog = () => status.log;

    console.log(`LoggerFunctional ===> ${JSON.stringify(status.items, null, 2)}`);
    let appInterface = <div className="App-content fadeIn">
                            <div className="flex3Column"></div>
                            <div className="flex3Column">
                                <Loader />
                            </div>
                            <div className="flex3Column"></div>
                        </div>;
    if (status.isLoaded) {
        appInterface = <div className="App-content fadeIn">
            <div className="flex3Column"></div>
            <div className="flex3Column">
                <LogEntry
                    logId={status.logID}
                    onChange={updateLog} 
                    getStateLog={getStateLog} 
                    title="Session Log" 
                    message="Add your session data"  
                    buttonLabel="submit" 
                    items={status.items}
                />
            </div>
            <div className="flex3Column"></div>
        </div>

    }
    return (
        appInterface            
    )

}
export default Logger;
