import React from 'react';

class SlideShow extends React.Component {
    slideShow = true;
    toggleSlideShow = () => {
        this.slideShow = !this.slideShow;
        this.getAnImage(this.index)
    }
    getAnImage = (css, path, imgArray, i) => {
        console.log(`getImageSlideShow => ${i}`)
        let url1 = path + imgArray[i].image;
        let url2 = path + imgArray[i+30].image;
        let url3 = path + imgArray[i+60].image;
        //console.log(url1);
        //console.log(url2);
        //console.log(url3);
        console.log(`slideShow: ${this.slideShow}`)
        return <div>
                    <img src={url1} alt="California Sur" onClick="this.toggleSlideShow()" />
                    <img src={url2} alt="North Baja Norte" onClick="this.toggleSlideShow()" />
                    <img src={url3} alt="Baja Sur" onClick="this.toggleSlideShow()" />
                </div>
    }
    getImageSlideShow = (css, path, imgArray, index) => {
        index++
        if (index >= imgArray.length/3) { index = 1; }
        setTimeout(() => this.getAnImage(css, path, imgArray, index), (1000))
    }
    init = () => {
        let x=1;
        let images = [];
        for(let x=1;x<=90;x++) {
            if (x<31) {
                images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/socal/local/socal_large_"+x+".png" })
            } else if (x < 61) {
                images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/nbaja/puntabaja/nearshore/puntabaja_large_"+(x-30)+".png" })
            } else {
                images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/sbaja/local/sbaja_large_"+(x-60)+".png" })
            }
        }
        return this.getImageSlideShow("swell", "", images, 0);
    }
    render() {
        return (
            this.init()
        )
    }
}

export default SlideShow;
