import React from 'react';
import ReactDOM from 'react-dom';

class Timer extends React.Component {

    render() {
        return (
            <div className="time mt-40">
                <div className='color-red'>do you know what time it is?</div><br/><b>{this.props.date.toLocaleTimeString().replace(" ","").toLocaleLowerCase()}</b><br/><br/><div className='color-green'><b>{ this.props.timeItIsNow }</b></div>
            </div>
           /*
           <div>
                <h1>Hello, world!</h1>
                <h2>It is {this.props.date.toLocaleTimeString()}.</h2>
            </div>
            */
        )
    }
}

