import React from 'react';
class TextColorizer extends React.Component {
    text;
    colors = ["color-green", "color-yellow", "color-red"];
    colorIndex = -1;

    output = "";
    getNextColor = () => { 
        this.colorIndex = this.colorIndex + 1;
        return this.colors[this.colorIndex];
    };
    constructor(text) {
        super();
        this.text = text.text;
        console.log(`text: ${text}`)
    }
    letters = this.text.split('');
    colorize = (value, index, array) => {
        //console.log("index: " + index); 
        //console.log("array: " + array); 
        //console.log("value: " + value);
        const character = value;
        this.output =+ `<span className="${this.getNextColor(this.colorIndex)}">${character}</span>`;
    }
    render() {
        this.letters.forEach(this.colorize);
        return <div className="bigHeader">{this.output}</div>;
    }
}

export default TextColorizer;
