import React from 'react';
import shakaBlack from '../assets/images/shakaBlack.png';
import thumbsUp from '../assets/images/ThumbsUp.png';
import thumbsDown from '../assets/images/ThumbsDown.png';
/*
<FormRadio 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class FormRadio extends React.Component {

    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            label: props.label,
            groupTitle: props.groupTitle,
            items: props.items
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange = (event) => {
        console.log(`handleChange => ${event.target.value} index: ${event.target.id}`)
        this.setState({
            selected: event.target.id,
            value: event.target.value
        });
    }
    selectItems() {
        const reportAwesome = () => console.log('Firing brah!');
        const reportGood = () => console.log('Fun kine!');
        const reportBad = () => console.log('At least I got wet...');
        const badIcon = (select) => {
            const displayClass = (select === true) ? "shakingShaka shaka" : "shaka";
            return <div className="flex3Column bg-red pt-10 pb-5 radius-10 m-5" onClick={reportBad}>
                <img id="bad" src={thumbsDown} alt="bad" className={displayClass} />
            </div>;
        }

        const goodIcon = (select) => <div className="flex3Column bg-yellow pt-10 pb-5 radius-10 m-5" onClick={reportGood}>
                <img id="good" src={thumbsUp} alt="good" className="shaka" />
            </div>;

        const awesomeIcon = (select) => <div className="flex3Column bg-neogreen pt-10 pb-5 radius-10 m-5" onClick={reportAwesome}>
                <img id="awesome" src={shakaBlack} alt="awesome" className="shaka shakingShaka" />
            </div>;

        const icon = (itemValue, select) => {
            let iconOut = "";
            if (itemValue === "Firing") {
                iconOut = awesomeIcon(select);
            } else if (itemValue === "Good") {
                iconOut = goodIcon(select);
            } else {
                iconOut = badIcon(select); 
            }
            console.log(`Value: ${itemValue}`)
            return iconOut;
        } 


        return this.state.items.map((item, index) =>
            <div className="flex3Column" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} >
                <input id={index} key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} type="radio" value={item.toString()} onChange={this.handleChange} checked={Number(this.state.selected) === Number(index) ? true : false}/>
                <span className="description" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))}>{item.toString()}</span>
                {icon(item.toString(), (Number(this.state.selected) === Number(index)) ? true : false)}
            </div>
        )

    } 
    handleSubmit(event) {
        event.preventDefault();
    };

    render() {
        return (
            <div className="m-5 p-20 m-5 flexContainer">    
                {this.selectItems()}
            </div>
        );
    };
}
export default FormRadio;
