import React, {useState} from 'react';
import getKey from '../utils/KeyGenerator.js';
import { openweatherAPI_KEY, openweatherAPI_BASE_URL } from '../../apis/config';
import N from '../../assets/images/windN.png'
import NE from '../../assets/images/windNE.png'
import E from '../../assets/images/windE.png'
import SE from '../../assets/images/windSE.png'
import S from '../../assets/images/windS.png'
import SW from '../../assets/images/windSW.png'
import W from '../../assets/images/windW.png'
import NW from '../../assets/images/windNW.png'

const CitySelector = () => {
    //api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}
    //Metric
    //api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}&units=metric
    //Image Source
    //http://openweathermap.org/img/wn/10d@2x.png
    const key = openweatherAPI_KEY;
    const baseUrl = openweatherAPI_BASE_URL;

    const [city, setCity] = useState('Carlsbad, CA');
    const [results, setResults] = useState('');
    const urlEnd = `/data/2.5/forecast?q=${city},us&appid=`;

    const displayResults = (results) => {
        const list = results.list.map((item) => {
            const main = item.main;
            const temp = main.temp.toFixed(1);
            const temp_min = main.temp_min.toFixed(1);
            const temp_max = main.temp_max.toFixed(1);
            const feels_like = `feels like: ${main.feels_like}Â°`;
            const temperature = <div>
                            <div className='color-yellow bold mb-10'>temperature: </div>
                            <div className='r-5 p-10 bg-dkGreen'>
                                {temp}Â°<br/>
                                <span className='copyright'>{temp_min}Â°</span>
                            </div>
                        </div>;

            const humidity = <div>
                                <div className='color-yellow bold mb-10'>humidity: </div>
                                <div className='r-5 p-10 bg-dkGreen'>
                                    {main.humidity}Â°
                                </div>
                            </div>
            const weather = item.weather[0];
            const mainDescription = weather.main;
            const description = weather.description;
            const icon = weather.icon;
            const wind = item.wind;
            const windSpeed = () => <div>
                                        {wind.speed.toFixed(0)}
                                        <span className='copyright'> mph</span>
                                    </div>;
            const angleArray = [ 0, 25, 45, 65, 90, 115, 135, 160, 180, 205, 225, 250, 270, 295, 315, 340]
            const directionArray = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
            const getWindDirection = (direction) => {
               const windAngle = direction.toFixed(0);
               const angleIndex = angleArray.findIndex(angle => angle >= windAngle);
               return directionArray[angleIndex];
            }
            const windDirection = () => <div>{wind.deg}Â°</div>;
            const windDisplay = () => <div>
                                            <div className='color-yellow bold mb-10'>wind: </div>
                                            <div className='r-5 p-10 bg-dkGreen'>
                                                {getWindDirection(wind.deg)}
                                                {windDirection()}
                                                {windSpeed()}
                                            </div>
                                        </div>
            const visibility = <div>
                                    <div className='color-yellow bold mb-10'>visibility: </div>
                                    <div className='r-5 p-10 bg-dkGreen'>
                                        {item.visibility}
                                        <span className='copyright'> ft</span>
                                    </div>
                            </div>;
            const time = item.dt_txt;
            const cards = [temperature, windDisplay(), humidity, visibility]

            const card = (matchKind) => <div key={getKey('card')} className='flex4Column bg-lite mr-5 ml-5 p-10 r-10'>
                            {/*this.getMatchIcon(matchKind)*/}
                            <div className='greet'>{matchKind}</div>
                        </div>;
            return (
                <div className='width100Percant p-20 m-20 r-10 bg-dkGreen'>
                    <div className='bg-green r-10 p-20 m-20'>
                        <div className='color-yellow'>{time}</div>
                        <div className='white size25 p-5 mt-5'>{mainDescription}</div>
                        <div className='white greet p-5'>{description}</div>
                    </div>
                    <div className='width100Percent flexContainer'>
                        {cards.map((item) => card(item))}
                    </div>
                </div>
            )
        })
        console.log(`display: ${JSON.stringify(results, null, 2)}`)
        const display = list;
        console.log(`display: ${display}`)
        setResults(display)
    }

    const onSearch = () => {
        fetch(`${baseUrl}${urlEnd}${key}`)
        .then((response) => response.json())
//        .then((result) => console.log(`${JSON.stringify(result,null, 2)}`))
        .then((results) => displayResults(results));
    };
    const onKeyDown = (event) => {
        if (event.keyCode === 13) {
            onSearch();
        }
    };
    return (
        <div>
            <h1 className='navBranding color-yellow'>Search your city</h1>
            <input onKeyDown={onKeyDown} value={city} placeholder='Enter city'
                    onChange={(event) => setCity(event.target.value)} className='greet p-20 r-10 w-200 brdr-green'/>
            <button onClick={onSearch} className='ml-5 greet p-20 r-10 w-200 bg-green brdr-green'>
                Check Weather
            </button>
            <h1 className='navBranding color-yellow'>Results:</h1>
            <div className='color-neogreen'>{results}</div>
        </div>
    );
};

export default CitySelector;
