import React from 'react';
import ReactDOM from 'react-dom';
import CustomSpan from './CustomSpan.js'

class Lister extends React.Component {
    text;
    colors = ["color-green", "color-yellow", "color-red"];
    colorIndex = -1;
    output = "";
    getNextColor = () => { 
        this.colorIndex = this.colorIndex + 1;
        return this.colors[this.colorIndex];
    };
    constructor(text) {
        super();
        this.text = text.text;
    }
    //letters = this.text.split('');
    colorize = (value, index, array) => {
        //console.log("index: " + index); 
        //console.log("array: " + array); 
//        console.log("value: " + value);
        const character = value;
        this.output = this.output + `<span className="${this.getNextColor()}">${character}</span>`;
    }
    render() {
        //this.text.split('').forEach(this.colorize);
        //console.log(this.output);
        //return `<div className="bigHeader">${this.output}</div>`;
        return (
            <div className='bigHeader'>
                {this.text.split('').map(function(listValue){
                    return {listValue};
                })}
            </div>
          );
    }
}

export default Lister;
