import React from 'react';

class Timer extends React.Component {
    element;
    constructor(){
        super();
        this.element = (<div></div>);   
    }
    clock = (function() {
        let timeItIs = [
            "shaka time!!!",
            "get a wave brah!",
            "keep froth alive...",
        ];
        let x = 0;
        let interval = 0;
        let timeItIsNow = timeItIs[x];
        setInterval(timer , 1000)
        function timer() {
            this.element = (
                <div className="time mt-40">
                <div className='color-red'>do you know what time it is?</div><br/><b>{new Date().toLocaleTimeString().replace(" ","").toLocaleLowerCase()}</b><br/><br/><div className='color-green'><b>{ timeItIsNow }</b></div>
                </div>
            );
            interval = (interval === 3) ? 0 : (interval+1);
            x = (x === (timeItIs.length-1) && (interval === 0)) ? 0 : ((interval === 0) ? (x+1) : x);
            timeItIsNow = timeItIs[x];
            console.log(timeItIsNow)
            return(
                <div id="time">{ this.element }</div>
            )
        }
    })();

    render(){
        return(
            <div id="time">{ this.element }</div>
        )
    }
}

export default Timer;
