import React from 'react';
//import 'bootstrap/dist/css/bootstrap.min.css';
import getKey from '../utils/KeyGenerator.js';
import { openweatherAPI_KEY, openweatherAPI_BASE_URL } from '../../apis/config';

//api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}
//Metric
//api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}&units=metric
//Image Source
//http://openweathermap.org/img/wn/10d@2x.png
const key = openweatherAPI_KEY;
const baseUrl = openweatherAPI_BASE_URL;
const city = 'London'
const urlEnd = `/data/2.5/forecast?q=${city},us&appid=`;

const WeatherCard = ({dt, temp_min, temp_max, main, icon}) => {

  const date = new Date(dt);
  const src =`http://openweathermap.org/img/wn/${icon}@2x.png`;

  return (
    <div>
        <div className='bg-dkGreen p-20 r-10 m-20' style={{width: '18rem'}}>
            <div className='bg-white r-10 mb-20 width-100-percent'>
                <img src={src} alt='weather' />
            </div>
            <h1 className='color-yellow'>{main}</h1>
            <h4 className='white'>{date.toLocaleDateString()} - {date.toLocaleTimeString()}</h4>
            <div className='color-yellow bold'>high: {temp_max}째</div>
            <div className='color-neogreen bold'>low: {temp_min}째</div>
        </div>

        <div key={getKey("loc")} /*onClick={() => this.props.editLocation()}*/>
            <div className="r-10 m-10 p-20 bg-dkGreen">
                    <div className="width100Percent flexContainer">{this.getStars(matches)}</div>
                    <div className="mt-10 navBranding">{main}</div>
                    <div className="greet color-yellow p-5 bg-dkGreen mt-15 mb-10 r-5">{date.toLocaleDateString()} - {date.toLocaleTimeString()}</div>
                <div className="flexContainer">
                    <div className="flexContainer m-auto">
                        <div className="columnRight pr-10">
                            <div className="color-neogreen bold">high: </div>
                            <div className="color-neogreen bold">low: </div>
                        </div>
                        <div className="columnLeft">
                            <div>{temp_max}째</div>
                            <div>{temp_min}째</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
  );
};

export default WeatherCard;
