import React, { useState } from 'react';
export default function Todos(props) {

    const [todos, setTodos] = useState(['Learn Hooks', 'Implement hooks']);
//    const [cart, setCart] = useState({id: 'xx22', name: 'iphone 8', quantity: 1});
    const addTodo = () => console.log(document.getElementById("todo").value);
    return (
        <div>
            <div className="color-yellow p-5 r-10 flexContainer">
                <div className="flex3Column"></div>
                <div className="p-5 r-10 flex3Column bg-darker">
                    <label>
                        Add an item:
                        <input type="text" id="todo" name="todo" />
                    </label>
                </div>
                <div className="flex3Column"></div>
            </div>
            <div className="flexContainer">
                <div className="flex3Column"></div>
                <div className="flex3Column">
                    <button className="r-10 greet bold bg-yellow m-5 p-20" onClick={() => addTodo()}>ADD</button>
                </div>
                <div className="flex3Column"></div>
            </div>
        </div>
    );
}
