import React, { forwardRef, useImperativeHandle } from 'react';
import PostDirectory from './PostDirectory.js';
import templateData from './TemplateData.js';

const LogId = forwardRef((logId, ref) => {

    useImperativeHandle(
        ref,
        () => ({
            showAlert() {
                alert("Child Function Called")
            }
        }),
    )

    const postDirectory = new PostDirectory();
    logId = (logId === null || logId === undefined) ? postDirectory.getLastId() : logId;
    const log = (JSON.parse(localStorage.getItem(logId)) === null) ? templateData : JSON.parse(localStorage.getItem(logId));
    const getDate = () => log.Day.Date;
    const generateNewLogId = () => {
        const date = new Date()
        const st = date.toDateString().replace(/ /g,"");
        const nd = date.toLocaleTimeString().replace(/ /g,"");
        localStorage.setItem("lastPostId", `${st}${nd}`);
        const newId = `${st}${nd}`;
        console.log(`LogId: generateNewLogId => logId: ${newId}`);
        return newId;
    }
    /*
    const setLogId = (logId, log) => this.setState({
        logId: logId,
        log: log
    });
    */
    const getLogId = () => logId;  
    console.log(`LastLogId => render: this.state.logId: ${logId} - this.state.log: ${log}`);
    return <p>Count: {postDirectory.length}</p> 
})
export default LogId;
