import React, { useState, useEffect } from 'react';

const GallonsCalculator = () => {

  const [exchangeRate, setExchangeRate] = useState(0);
  const [pricePerLiter, setPricePerLiter] = useState(0);
  const [pricePerLiterUSD, setPricePerLiterUSD] = useState(0);
  const [litersPurchased, setLitersPurchased] = useState(0);
  const [gallons, setGallons] = useState(0);
  const [usdPerGallon, setUsdPerGallon] = useState(0);
  const [totalUSD, setTotalUSD] = useState(0);
  const [totalPesos, setTotalPesos] = useState(0);

  useEffect(() => {
    const newPricePerLiter = Number(pricePerLiter / exchangeRate)
    if (newPricePerLiter === Number) setPricePerLiterUSD(newPricePerLiter);
    const newGallons = Number(litersPurchased * 0.264172);
    if (newGallons === Number) setGallons(newGallons.toFixed(2));
    const newUsdPerGallon = Number(pricePerLiterUSD * 3.78541);
    if (newUsdPerGallon === Number) setUsdPerGallon(newUsdPerGallon.toFixed(2));
    const newTotalUSD = Number(gallons * usdPerGallon);
    if (newTotalUSD === Number) setTotalUSD(newTotalUSD.toFixed(2));
    const newTotalPesos = Number(litersPurchased * pricePerLiter);
    if (newTotalPesos === Number) setTotalPesos(newTotalPesos.toFixed(2));
  }, [
    pricePerLiter, 
    exchangeRate, 
    litersPurchased, 
    pricePerLiterUSD, 
    gallons, 
    usdPerGallon, 
    litersPurchased,
    pricePerLiter
  ]);

  return (
    <div>
      <h1>Gallons Calculator</h1>
      <div>
        <label>
          Exchange Rate (foreign currency to USD):
          <input
            type="number"
            value={exchangeRate}
            onChange={(e) => setExchangeRate(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Price per Liter (in foreign currency):
          <input
            type="number"
            value={pricePerLiter}
            onChange={(e) => setPricePerLiter(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Liters Purchased:
          <input
            type="number"
            value={litersPurchased}
            onChange={(e) => setLitersPurchased(e.target.value)}
          />
        </label>
      </div>
      <div>Gallons purchased:</div>
      <div>{gallons}</div>
      <div>Dollars per gallon:</div>
      <div>${usdPerGallon}</div>
      <div>Total dollars:</div>
      <div>${totalUSD}</div>
      <div>Total pesos:</div>
      <div>${totalPesos}</div>
    </div>
  );
};

export default GallonsCalculator;
