import React, { useState } from 'react';

const GallonsCalculator = () => {
  const [exchangeRate, setExchangeRate] = useState('');
  const [pricePerLiter, setPricePerLiter] = useState('');
  const [litersPurchased, setLitersPurchased] = useState('');
  const [resultsDisplay, setResultsDisplay] = useState(`Enter values and press 'Calculate'`);

  const calculate = () => {
    // Convert pricePerLiter from foreign currency to USD
    const pricePerLiterUSD = pricePerLiter / exchangeRate;

    // Calculate gallons
    const gallons = litersPurchased * 0.264172;

    // Calculate USD per gallon
    const usdPerGallon = pricePerLiterUSD * 3.78541;
    const usdPerGallonDisplay = usdPerGallon.toFixed(2);

    return { gallons, usdPerGallonDisplay };
  };

  const handleCalculate = () => {
    const { gallons, usdPerGallon } = calculate();
    const totalUSD = (gallons * usdPerGallon);
    const totalUsdDisplay = totalUSD.toFixed(2)
    setResultsDisplay(`Gallons purchased: ${gallons}, USD per gallon: $${usdPerGallon} total dollars: $${totalUsdDisplay}`);
  };

  return (
    <div>
      <h1>Gallons Calculator</h1>
      <div>
        <label>
          Exchange Rate (foreign currency to USD):
          <input
            type="number"
            value={exchangeRate}
            onChange={(e) => setExchangeRate(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Price per Liter (in foreign currency):
          <input
            type="number"
            value={pricePerLiter}
            onChange={(e) => setPricePerLiter(e.target.value)}
          />
        </label>
      </div>
      <div>
        <label>
          Liters Purchased:
          <input
            type="number"
            value={litersPurchased}
            onChange={(e) => setLitersPurchased(e.target.value)}
          />
        </label>
      </div>
      <button onClick={handleCalculate}>Calculate</button>
      <div>{resultsDisplay}</div>
    </div>
  );
};

export default GallonsCalculator;
