import React, { useState, useEffect } from 'react';
import getKey from '../utils/KeyGenerator.js';

const TripFuelTracker = () => {

    const [location, setLocation] = useState('');
    const [exchangeRate, setExchangeRate] = useState(0);
    const [pricePerLiter, setPricePerLiter] = useState(0);
    const [litersPurchased, setLitersPurchased] = useState(0);
    const [trips, setTrips] = useState([]);

    const handleCalculateAndRecord = () => {
        const pricePerLiterUSD = pricePerLiter / exchangeRate;
        const gallons = litersPurchased * 0.264172;
        const newGallons = gallons.toFixed(1);

        const usdPerGallon = pricePerLiterUSD * 3.78541;
        const newUsdPerGallon = usdPerGallon.toFixed(2);
        const totalUSD = gallons * usdPerGallon;
        const newTotalUSD = totalUSD.toFixed(2);

        const newTrip = {
            location: location,
            gallons: newGallons,
            usdPerGallon: newUsdPerGallon,
            totalUSD: newTotalUSD
        }
        const newTrips = [...trips, newTrip]
        setTrips(newTrips);

    };

    return (
        <div>
            <h1 className='color-soft'>Trip Fuel Tracker</h1>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Location:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='string'
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Exchange Rate:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={exchangeRate}
                        onChange={(e) => setExchangeRate(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Pesos per Liter:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={pricePerLiter}
                        onChange={(e) => setPricePerLiter(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Total Liters:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={litersPurchased}
                        onChange={(e) => setLitersPurchased(e.target.value)}
                    />
                </div>
            </label>
            <button className='myButton mt-20' onClick={handleCalculateAndRecord}>Add</button>
            <div className='menuHeight bg-DARK'>
            {
                trips.map((trip, index) => <div className='mt-20 color-soft' key={getKey(`trip${index}`)}>
                    <div className='flexContainer bg-tinted r-5 m-5'>
                        <div className='p-5'>
                            <span className='p-5 bold'>{index + 1}</span>
                            <span className='p-5 bold r-5 mt-5 mb-5'>{trip.location}</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>{trip.gallons}</span>
                            <span className='p-5'>gallons</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>${trip.usdPerGallon}</span>
                            <span className='p-5'>per gallon</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>${trip.totalUSD}</span>
                            <span className='p-5'>dollars</span>
                        </div>
                    </div>
                </div>
                )
            }
            </div>
        </div>
    );
};

export default TripFuelTracker;
