import React, { useState, useEffect } from 'react';
import getKey from '../utils/KeyGenerator.js';
export default function Todos(props) {
    const initTodos = [
        {
            description: 'Eat',
            completed: false
        },
        {
            description: 'Surf',
            completed: false
        },
        {
            description: 'Sleep',
            completed: false
        }
    ]
    const getLocal = () => (localStorage.getItem('todos') === 'null') ? initTodos : JSON.parse(localStorage.getItem('todos'));
    const [todos, setTodos] = useState(getLocal());
    const storeIt = () => localStorage.setItem('todos', JSON.stringify(todos));
    const todoInput = () => document.getElementById("todo").value;
    const clearInput = () => document.getElementById("todo").value = "";
    const getNewTodo = (todo) => todo.charAt(0).toUpperCase() + todo.slice(1);

    useEffect(() => {
        storeIt();
    }, [todos]);

    const addTodo = () => {
        const newTodo = {
            description: getNewTodo(todoInput()),
            completed: false
        }
        console.log(`new todo: ${newTodo.description}`);
        const newTodos = [...todos];
        newTodos.push(newTodo);
        setTodos(newTodos);
        clearInput();
    };
    const removeTodo = e => {
        const id = e.target.id;
        console.log(`remove todo: ${todos[id]}`);
        const newTodos = [...todos];
        newTodos.splice(id, 1)
        setTodos(newTodos);
    }
    function makeMenu() {
        const makeMenu = (menu) => menu.map(item => <div className="button bg-green r-10 m-5 p-5">{item}</div>);
        const menuData = ["one", "two", "thee"];
        return (
            <React.Fragment>{makeMenu(menuData)}</React.Fragment>
        )
    }
    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            addTodo();
        }
    };
    const toggleCheckbox = (index) => {
        const newTodos = [...todos];
        newTodos[index].completed = !newTodos[index].completed;
        setTodos(newTodos);
    };
    return (
        <div>
            <div className="color-yellow p-5 r-10">
                <div className="p-5 r-10 bg-tinted">
                    <label className="flexContainer">
                        <input className="ht-30 m-5 flex2Column contentCenter p-15 r-10 bg-darker color-white bold size25" type="text" id="todo" name="todo" placeholder="Enter a todo" onKeyDown={handleKeyDown} />
                        <button className="r-10 greet bold bg-green m-5 p-10 flex2Column10Percent contentCenter" onClick={() => addTodo()}>ADD</button>
                    </label>
                </div>
            </div>
            <div className="p-5 r-10">
                <div id="list" className="p-5 r-10 bg-tinted">
                    {todos.map((todo, index) => (
                        <div key={getKey("todo")} className="p-10 flexContainer bg-darker r-10 m-5">
                            <div className="color-lite flex2Column p-10 size25 columnLeftAlign">
                                <input className='regular-checkbox button glassy mr-10' checked={todo.completed} type='checkbox' id='completed' onChange={() => toggleCheckbox(index)} />
                                {todo.description}
                            </div>
                            <div className="flex2Column10Percent columnRightAlign">
                                <button id={index} name={todo.description} className="r-10 p-15 greet bold bg-red m-5" onClick={(e) => removeTodo(e)}>X</button>
                            </div>
                        </div>
                    ))}
                    {/*makeMenu()*/}
                    <div className='flexContainer'>
                        <div className="button r-10 bg-darker color-lite flex3Column p-10 size25 columnCenterAlign">
                            RESET
                        </div>
                        <div className="button r-10 bg-darker color-lite flex3Column p-10 size25 columnCenterAlign">
                            EDIT
                        </div>
                        <div className="button r-10 bg-darker color-lite flex3Column p-10 size25 columnCenterAlign">
                            CLEAR
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
