import React from 'react';
import shakaBlack from '../assets/images/shakaBlack.png';
import thumbsUp from '../assets/images/ThumbsUp.png';
import thumbsDown from '../assets/images/ThumbsDown.png';
/*
<FormRadio 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class FormRadio extends React.Component {

    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            label: props.label,
            groupTitle: props.groupTitle,
            items: props.items
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange = (event) => {
        this.setState({
            selected: event.target.id,
            value: event.target.value
        });
    }
    selectItems() {
        const reportAwesome = () => this.setState({
            selected: 0,
            value: 'Awesome'
        });
        const reportGood = () => this.setState({
            selected: 1,
            value: 'Good'
        });
        const reportBad = () => this.setState({
            selected: 2,
            value: 'Bad'
        });

        const getIcon =(value, select) => {
            const displayClass = (select === true) ? "shakingShaka shaka" : "shaka";

            let displayColor = (value === "Firing") ? "bg-neogreen" : "bg-yellow";
            displayColor = (value === "Bad") ? "bg-red" : displayColor;

            const buttonClass = displayColor + " flex3Column pt-10 pb-5 radius-10 m-5";
            let click = (value === "Firing") ? reportAwesome : reportGood;
            click = (value === "Bad") ? reportBad : click;

            let icon = (value === "Firing") ? shakaBlack : thumbsUp;
            icon = (value === "Bad") ? thumbsDown : icon;

            return <div className={buttonClass} onClick={click}>
                <img id={value} src={icon} alt={value} className={displayClass} />
            </div>;
        }

        const icon = (itemValue, select) => {
            let iconOut = "";
            iconOut = getIcon(itemValue, select);
            return iconOut;
        } 


        return this.state.items.map((item, index) =>
            <div className="flex3Column" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} >
                <input id={index} key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} type="radio" value={item.toString()} onChange={this.handleChange} checked={Number(this.state.selected) === Number(index) ? true : false}/>
                <span className="description" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))}>{item.toString()}</span>
                {icon(item.toString(), (Number(this.state.selected) === Number(index)) ? true : false)}
            </div>
        )

    } 
    handleSubmit(event) {
        event.preventDefault();
    };

    render() {
        return (
            <div className="m-5 p-20 m-5 flexContainer">    
                {this.selectItems()}
            </div>
        );
    };
}
export default FormRadio;
