        <div className="m-20">
            <div className='directory sides-auto'>
                <input value={text} onChange={(e) => setText(e.target.value)} />
                <DropDown 
                    label='Select a language:' 
                    options={options} 
                    selected={selected} 
                    onSelectionChange={setSelected}
                />
            </div>
        </div>
    )
};

export default Translator;
