import React from 'react';
const DropDown = ({ label, options, selected, onSelectionChange}) => {
    const list = options.map((color) => {
        return (
            <div key={color.value} className={`item button pointer r-5 p-10 mr-20 ml-20 bold mb-1 bg-${color.value}`}>
                {color.label}
            </div>
        )
    })
    return (
        <div className='ui form'>
            <div className='field'>
                <label className='label'>{label}</label>
                <div className='ui selection dropdown visible active color-yellow greet normal'>
                    <i className="dropdown icon"></i>
                    <div className='text'>{selected.label}</div>
                    <div className='menu visible transition'>
                        {list}
                    </div>
                </div>

            </div>
        </div>
    )
}
export default DropDown;
