import React, { useState } from 'react';

export default function Adder(props) {
  // Declare a new state variable, which we'll call "count"
    const units = [``, `km`, `mph`, `pts`, `kts`, `miles`, `ft`, `inche`, `'`, `"`];
    const [ count, setCount ] = useState(0);
	const [ unit, setUnit ] = useState(props.unit ? props.unit : ``);
    const [ label, setLabel ] = useState(props.label ? props.label : "How many?");

    const getUnitIndex = () => console.log(`UNITS: ${units.find(unitId => unitId === unit)}`);

    return (
        <div className="color-yellow">
            <p>
                {label}
            </p>
            <p>
                {count}{unit}
            </p>
            <button onClick={() => setCount(prevCount => prevCount - 1)}>-</button>
            <button onClick={() => setCount(prevCount => prevCount + 1)}>+</button>
            <button onClick={() => setUnit(units[getUnitIndex()+1])}>unit</button>
        </div>
    );
}
