import React, { useState } from 'react';

export default function Counter() {
  // Declare a new state variable, which we'll call "count"
  const [count, setCount] = useState(0);
  const onButtonClick = () => {
    setCount(count + 1)
  };
  return (
    <div className="greet white bg-darker r-10 p-10 m-20">
      <div className='p-10'>You clicked {count} times</div>
      <button className='m-10 bg-green p-10 bold r-10' onClick={() => setCount(count + 1)}>
        Click me
      </button>
      <button className='m-10 bg-green p-10 bold r-10' onClick={onButtonClick}>or click Me!</button>
    </div>
  );
}
