    const findTwoHighestIndices = (scores) => {
        let highestIndex = -1;
        let secondHighestIndex = -1;
        let highestScore = -Infinity;
        let secondHighestScore = -Infinity;

        for (let i = 0; i < scores.length; i++) {
            if (scores[i] > highestScore) {
                secondHighestIndex = highestIndex;
                secondHighestScore = highestScore;
                highestIndex = i;
                highestScore = scores[i];
            } else if (scores[i] > secondHighestScore) {
                secondHighestIndex = i;
                secondHighestScore = scores[i];
            }
        }

        return [highestIndex, secondHighestIndex];
    };

