import React from 'react';

class SelectorForm extends React.Component {
    items = [
        "White Rice", 
        "Spring Mix", 
        "Ahi", 
        "Shrimp", 
        "Octopus", 
        "Salmon",
        "Albacore", 
        "Not spicy", 
        "Medium Spicy", 
        "Spicy", 
        "Eel sauce", 
        "Miso sauce", 
        "Wasabi", 
        "Cucumber",
        "Onion",
        "Imitation crab", 
        "Pineapple", 
        "Mandorine Orange", 
        "Raddish",
        "Edamame", 
        "Ginger", 
        "Seaweed Salad", 
        "Masago", 
        "Green onion", 
        "Fried garlic",
        "Tempura crisp", 
        "Furikake", 
        "Dried seaweed", 
        "Sesame Seeds",
        "Extra eel",
        "Exra Miso"
      ];
    header = "Select:";
    bowlIngredients = "";
    constructor(props) {
        super(props);
        this.state = {value: props.selected};
        this.items = props.food;
        this.header = props.header;
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        console.log('You Selected: ' + event.target.value);
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        console.log('Items: ' + this.items.length);
        console.log('You Submitted: ' + this.state.value);
        event.preventDefault();
    }
    selectItems = this.items.map((item) =>
        <option 
            key={item.toString().toLowerCase()} 
            value={item.toString()}>{item}
        </option>    
    );

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <textarea id="bowl" rows="11" cols="10" value={bowlIngredients} onChange={showBowlIngredients} /><br/>
                <label>
                    {this.header}<br/>
                    <select value={this.state.value} onChange={this.handleChange}>
                        {this.selectItems}
                    </select>
                </label><br/>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default SelectorForm;
