import React, {useState, useEffect} from 'react';
import LogEntry from './LogEntryFunctional.js';
import Loader from '../utils/Loader.js';
import LogId from './LogId.js';
import interfaceData from './InterfaceData.js'
import templateData from './TemplateData.js';
const Logger = ({logId, location}) => {

    const [status, setStatus] = useState({
        logID: logId,
        log: templateData,
        spot: localStorage.getItem("spot"),
        isLoaded: false,
        items: [],

    })
    const [logID, setLogID] = useState();
    const [log, setLog] = useState();
    const [spot, setSpot] = useState();
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const logIdComponent = new LogId(logID);

    if (localStorage.getItem(logIdComponent.getLogId()) === null) {
        const newId = logIdComponent.generateNewLogId();
        setStatus({
            logID: newId,
            log: templateData,
            spot: localStorage.getItem("spot"),
            isLoaded: false,
            items: [],
        })
    } else {
        setStatus({
            logID: logIdComponent.getLogId(),
            log: JSON.parse(localStorage.getItem(logIdComponent.getLogId())),
            spot: localStorage.getItem("spot"),
            isLoaded: false,
            items: [],
        })
    }    

    useEffect(() => {
        const { state } = location;
        console.log(`LoggerFunctional => componentDidMount -> SPOT: ${spot}`); 
        const logId = logID;
        let data = interfaceData;
        const returnJSON = (response) => response.json();
        const returnRejection = (response) => Promise.reject({status: response.status, data});
        const validate = (response) => (response.ok) ? returnJSON(response) : returnRejection(response);
        /*
        const requestInit = {
            method:'GET',
            headers: {
                'Content-Type': 'application/json'
            },
            mode: 'cors',
            cache: 'default'
        };
        */
        //const uri = new Request('https://jsongithub.github.io/portfolio/assets/data/appData.json', requestInit);
        //GOOD const uri = 'https://jsongithub.github.io/portfolio/assets/data/appData.json';
        const uri = 'https://jsongithub.github.io/portfolio/assets/data/appData.json';
        //const uri = 'localhost:8080/writeSurfLog.json';
        /*
        fetch(uri)
            .then(response => validate(response))
            .then(data => {
                setIsLoaded(true);
                setItems(data);
                setLogID(logId);
            })
            .catch(err => console.log(`Something went wrong!\nuri: ${uri} \npath: ${window.location.pathname}\n`, err));
        */
       if (localStorage.getItem(logIdComponent.getLogId()) === null) {
            setStatus({
                logID: logIdComponent.generateNewLogId(),
                log: templateData,
                spot: localStorage.getItem("spot"),
                isLoaded: false,
                items: [],
            })
        } else {
            setStatus({
                logID: logId,
                log: JSON.parse(localStorage.getItem(logIdComponent.getLogId())),
                spot: localStorage.getItem("spot"),
                isLoaded: true,
                items: data,
            }) 
        }
    })

    const updateLog = (groupTitle, label, selected, set) => {
        log[groupTitle][label] = selected;
        if (groupTitle !== undefined && groupTitle !== 1 && selected !== undefined && set) {
            setLog(log);
        }
    }
    const getStateLog = () => log;

    console.log(`LoggerFunctional ===> ${JSON.stringify(items, null, 2)}`);
    let appInterface = <div className="App-content fadeIn">
                            <div className="flex3Column"></div>
                            <div className="flex3Column">
                                <Loader />
                            </div>
                            <div className="flex3Column"></div>
                        </div>;
    if (isLoaded) {
        console.log(`LoggerFunctional => -> \nSPOT: ${spot}\nlogId: ${logID}\nlog: ${JSON.stringify(log, null, 2)}`);
        appInterface = <div className="App-content fadeIn">
            <div className="flex3Column"></div>
            <div className="flex3Column">
                <LogEntry
                    logId={logID}
                    onChange={updateLog} 
                    getStateLog={getStateLog} 
                    title="Session Log" 
                    message="Add your session data"  
                    buttonLabel="submit" 
                    items={items}
                />
            </div>
            <div className="flex3Column"></div>
        </div>

    }
    return (
        appInterface            
    )

}
export default Logger;
