import React from 'react';
import FormEssay from './components/forms/FormEssay.js';
import Reservation from './components/forms/FormReservation.js';
import SignUpDialog from './components/utils/SignUpDialog.js';
import Footer from './components/site/Footer.js';
import Header from './components/site/Header.js';
import Home from './components/Home.js';
import Calculator from './components/Calculator.js';
import BowlBuilder from './components/BowlBuilder.js';
import SurfLog from './components/Logger.js';
import './assets/css/App.css';
class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {isMotionOn: true};
  }

  company = "KFA";
  path = window.location.pathname;
  componentId = this.path.replace('/','').toLocaleLowerCase();
  currentComponent = this.componentId;
  setMotion = () => this.setState({ isMotionOn: !this.state.isMotionOn });
  content = document.getElementById('content');
  fadeIn = () => this.content.className = 'fadeIn';
  activateFade = () => setInterval(this.fadeIn, 1);
  components = {
    surflog: <SurfLog activateFade={this.activateFade()}/>,
    guestlist: <SignUpDialog title="Guest List" message="Sign up"  activateFade={this.activateFade}/>,
    reservation: <Reservation activateFade={this.activateFade}/>,
    essay: <FormEssay className='mt-40' activateFade={this.activateFade}/>,
    tempconverter: <Calculator activateFade={this.activateFade}/>,
    bowlbuilder: <BowlBuilder activateFade={this.activateFade}/>,
    home: <Home activateFade={this.activateFade}/>
  };

  render() {

    const componentTag = (current) => <div className="App">
                                <Header company={this.company} isMotionOn={this.state.isMotionOn} setMotion={this.setMotion}/>
                                <div id='content' className='o-0'>
                                  {this.components[current]}
                                </div>
                                <Footer isMotionOn={this.state.isMotionOn} setMotion={this.setMotion}/>
                              </div>;

    let AppComponent = () => componentTag(this.currentComponent);

    return (
      AppComponent()
    );
  }
}
export default App;
