import React from "react";

const initialTodos = [
  {
    id: "t1",
    task: "Code",
    complete: false
  },
  {
    id: "t2",
    task: "Cook",
    complete: false
  }
];
const todoReducer = (state, action) => {
    console.log(`todoReducer => `)
  switch (action.type) {
    case "ADD_TODO":
        console.log(`ADD_TODO:`)
      return state.map(todo => {
        if (todo.id === action.id) {
            console.log(`1a\ntodo.id: ${todo.id}\naction.id: ${action.id}\ntodo: ${JSON.stringify(todo, 2, null)}\ncomplete: ${!todo.complete}`)
          return { ...todo, complete: true };
        } else {
            console.log(`1b\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return todo;
        }
      });
    case "REMOVE_TODO":
        console.log(`REMOVE_TODO:`)
      return state.map(todo => {
        if (todo.id === action.id) {
            console.log(`2a\ntodo.id: ${todo.id}\naction.id: ${action.id}\ntodo: ${JSON.stringify(todo, 2, null)}\ncomplete: ${!todo.complete}`)
          return { ...todo, complete: false };
        } else {
            console.log(`2b\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return todo;
        }
      });
    default:
      return state;
  }
};
const App = () => {
  const [todos, dispatch] = React.useReducer(todoReducer, initialTodos);

  const handleChange = todo => {
    dispatch({
      type: todo.complete ? "REMOVE_TODO" : "ADD_TODO",
      id: todo.id
    });
  };
  const taskInterface = (todo) => {
    let taskClass = 'ml-5';
    if (todo.complete) {
        taskClass = taskClass + " color-green";
    } else {
        taskClass = taskClass + " color-yellow";
    }
    return <span className={taskClass}>
            {todo.task}
        </span>
  }
  return (
    <div>
        <ul>
        {todos.map(todo => (
            <li key={todo.id} >
                <label>
                    <div className='white b p-10'>
                        <input
                        type="checkbox"
                        checked={todo.complete}
                        onChange={() => handleChange(todo)}
                        />
                        {taskInterface(todo)}
                    </div>
                </label>
            </li>
        ))}
        </ul>
        <div className="button bg-dkGreen r-5 color-neogreen b p-10 m-10" onClick={addTask()}>Add</div>
    </div>
  );
};

export default App;
