import React, { useState } from 'react';
import Dialog from '../functional/Dialog.js';
import RadioSelector from '../forms/FormRadio.js';
import DatePicker from 'react-date-picker';
import getKey from '../utils/KeyGenerator.js';
import PostDirectory from './PostDirectory.js';
import {BrowserRouter as Link, Route} from 'react-router-dom';
import LogId from './LogId.js';
import selector from './Selector.js';
import group from './Group.js';
import templateData from './TemplateData.js';

const LogEntry = ({ logId, onChange, getStateLog, title, message, buttonLabel, items }) => {
    const [log, setLog] = useState(JSON.parse(localStorage.getItem(logId)));
    const [change, setChange] = useState(false);

    const posts = new PostDirectory();
    const [lastPostId, setLastPostId] = useState((localStorage.getItem("lastPostId") === null) ? posts.getLastItem() : localStorage.getItem("lastPostId"));
    let selectorStatus = [];
    const spot = localStorage.getItem('spot');
    /*
    if (logId !== undefined && logId !== "" ) {
        setLastPostId(logId);
        console.log(`$$ logId1: ${logId}`);
        setLog((localStorage.getItem(logId) === null) ? templateData : JSON.parse(localStorage.getItem(logId)));
    } else {
        setLastPostId("ThuApr3020209:19:28PM");
        console.log(`logId2: ${lastPostId}`)
        setLog(JSON.parse(localStorage.getItem("ThuApr3020209:19:28PM")))
    }    
    */
    const logIdComponent = new LogId({logId: logId, log: log});
    const date = useState(new Date());
    setLastPostId(logId);
    setChange(false); 

    const handleSelection = (groupTitle, label, selected) => {
        if (typeof groupTitle === "string") {
            log[groupTitle][label] = selected;
            onChange(groupTitle, label, selected, true);
            setLog(log);
            setChange(true);
        } 
    }
    const updateNotes = (event) => {
        handleSelection("Comments", "notes", event.target.value);
    }
    const handleSubmit = (event) => {
        //let { log } = this.state;
        //log = (log !== undefined && JSON.stringify(log, null, 2) !== "{}") ? log : this.log;
        console.log(`handleSubmit: ${JSON.stringify(log, null, 2)}`)
        const recordId = logIdComponent.generateNewLogId();
        console.log(`handleSubmit: recordId: ${recordId}`)
        let postDirectory = posts.getDirectory();
        let post = "";
        const logIt = () => {
            postDirectory.push(recordId);
            postDirectory = JSON.stringify(postDirectory);
            console.log(`postDirectory: ${postDirectory}`)
            post = JSON.stringify(log, null, 2);
            console.log(`post: ${post}`)
            localStorage.setItem(recordId, post);
            //localStorage.setItem("postDirectory", postDirectory);
            posts.add(recordId);
        }
        const selectorStatusComplete = (selectorStatus.includes(false)) ? window.confirm("Report is incomplete, submit anyway?") : true;
        if (selectorStatusComplete) {
            logIt();
        }
    }
    const handleSave = (event) => {
        //let { log, lastPostId } = this.state;
        //log = (log !== undefined && JSON.stringify(log, null, 2) !== "{}") ? log : this.log;
        console.log(`handleSave: ${log}`)
        const recordId = lastPostId;
        let post = "";
        const logIt = () => {
            post = JSON.stringify(log, null, 2);
            console.log(`post: ${post}`)
            localStorage.setItem(recordId, post);
        }
        const selectorStatusComplete = (selectorStatus.includes(false)) ? window.confirm("Report is incomplete, submit anyway?") : true;
        if (selectorStatusComplete) {
            logIt();
        }
    }
    const handleDelete = (event) => {
        const recordId = lastPostId;
        posts.delete(recordId);
    }
    const onDateChange = (date) => {
        const pickerDate = date;
        date = String(date);
        const day = pickerDate.getDate();
        const month = pickerDate.getMonth()+1;
        const year = pickerDate.getFullYear();
        handleSelection("Day", "Date", pickerDate);
        handleSelection("Day", "Day", day);
        handleSelection("Day", "Month", month);
        handleSelection("Day", "Year", year);
    }
    const getItems = () => items;
    const selected = (item, groupTitle) => ((item.selections.indexOf(log[groupTitle][item.description])) !== -1) ? true : false;
    const defaultSelection = (item, groupTitle) => (selected(item, groupTitle)) ? (item.selections.indexOf(log[groupTitle][item.description])) : 0; 

    const radioItems = (item, groupTitle) => {
        return (
            <RadioSelector
                header={groupTitle}
                groupTitle={groupTitle} 
                selected={defaultSelection(item,groupTitle)} 
                label={item.description} 
                items={item.selections} 
                onChange={handleSelection}
            />
        )
    };

    const radio = (item, groupTitle) => <div className="r-vw bg-green">
                {radioItems(item, groupTitle)}
            </div>;

    const selectionInterface = (item, groupTitle) => (item.type === 'radio') ? radio(item, groupTitle) : selector(item, groupTitle, spot, defaultSelection, handleSelection, selected);
    const groups = () => getItems().map((item) => {
        const headerClasses = 'subHeader color-yellow';
        const selectorClasses = 'greet p-vw bg-vdkGreen flex3Column';
        const groupClasses = (window.innerWidth < 500) ? "r-vw" : "flexContainer width-100-percent r-vw";
        const description = item.description;
        //console.log(`description: ${JSON.stringify(item,null,2)}`)
       return <div key={getKey("groupConainer")}>
                    <div key={getKey("groupHeader")} className={headerClasses}>
                        {item.description}
                    </div>
                    <div className={groupClasses} key={getKey("groupSubConainer")}>
                        {group(item).map((group) => 
                            <div key={getKey("selectorContainer")} className={selectorClasses}>
                                {selectionInterface(group, description)}
                            </div>
                        )}
                    </div>
                </div>
    });
    const categories = () => {
        selectorStatus = [];
        return <div className='description'>{groups()}</div>;
    }
    const getLogObject = () => log;
    const dateEntry = () => {
        const logExists = (log !== undefined && log !== null && JSON.stringify(log, null, 2) !== "{}") ? true : false;
        const stateLogDate = () => getLogObject().Day.Date;
        const getDate = () => (logExists === true) ? new Date(stateLogDate()) : new Date(date);
        return <React.Fragment>
                    <div className='mb-5 subHeader color-yellow'>Date</div>
                    <div className='flexContainer width-100-percent bg-vdkGreen'>
                        <DatePicker
                            onChange={onDateChange}
                            value={getDate()} 
                            className='p-vw bg-green flex3Column r-vw m-vw'
                        /><br/>
                    </div>
                </React.Fragment>
    }

    return (
        <Route>
            <Dialog title={title} message={message}>
                <form onSubmit={handleSubmit}>
                    {dateEntry()}
                    {categories()}
                    <br/>
                    <div className="mb-5">Additional Comments: </div>
                    <textarea 
                        rows="10" 
                        cols={window.innerWidth/15} 
                        value={log.Comments.notes} 
                        onChange={updateNotes} 
                        className="mt-10 greet p-10 r-10 brdr-green"
                    /><br/><br/>
                    <Link className="noUnderline color-black"
                        to="/LogDirectory"
                        onClick={() => handleSubmit()}>
                        <div onClick={handleSubmit} className="button m-1 greet p-20 r-10 bg-green brdr-green">
                            {buttonLabel}
                        </div>
                    </Link> 
                    <Link className="noUnderline color-black"
                        to="/LogDirectory"
                        onClick={() => handleSave()}>
                        <div className="button m-1 greet p-20 r-10 bg-yellow brdr-yellow">
                            save
                        </div>
                    </Link>
                    <Link className="noUnderline color-black"
                        to="/LogDirectory"
                        onClick={() => handleDelete()}>
                        <div  className="button m-1 greet p-20 r-10 bg-red brdr-red">
                            delete
                        </div>
                    </Link>
                    <PostDirectory />
                </form>
            </Dialog>
        </Route>
    );
}

export default LogEntry;
