import React, { useState } from 'react';
import PostDirectory from './PostDirectory.js';
import templateData from './TemplateData.js';
const LogId = (logId) => {

    const postDirectory = new PostDirectory();
    const [status, setStatus] = useState({
        logId: (logId === null || logId === undefined) ? postDirectory.getLastId() : logId,
        log: (JSON.parse(localStorage.getItem(logId)) === null) ? templateData : JSON.parse(localStorage.getItem(logId))
    })
    const getDate = () => status.log.Day.Date;
    const generateNewLogId = () => {
        const date = new Date()
        const st = date.toDateString().replace(/ /g,"");
        const nd = date.toLocaleTimeString().replace(/ /g,"");
        localStorage.setItem("lastPostId", `${st}${nd}`);
        const newId = `${st}${nd}`;
        console.log(`LogId: generateNewLogId => status.logId: ${newId}`);
        return newId;
    }
    const setLogId = (logId, log) => setStatus({
        logId: logId,
        log: log
    });
    const getLogId = () => status.logId;     
    console.log(`LastLogId => render: status.logId: ${status.logId} - status.log: ${status.log}`);
    return <p>Count: {postDirectory.length}</p> 
}
export default LogId;
